insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('body\r\n{\r\n	background-color: #665;	/* border/edge of the layout color */\r\n}\r\n#pageFrame, #masthead, #footer\r\n{	\r\n	background-color: #fff;	/* major background color */\r\n}\r\n#pageFrame\r\n{\r\n	/* image used as background color for left column ( #E9E8CA ) */\r\n	background: #e9e8cA;\r\n}\r\n#masthead, #footer\r\n{\r\n	background-color: #885;\r\n	color: #fff;\r\n}\r\n#footer\r\n{\r\n	border-bottom: solid 1px #997; /* IE 6 has the footer flow over the pageFrame\'s \r\n									  bottom border. if this border is added, IE draws\r\n									  the pageFrame border as exptected */\r\n}\r\n.hnav, .hnav ul li a\r\n{\r\n	background-color: #cc9;\r\n	color: #336;\r\n}\r\n.hnav ul li a:hover\r\n{\r\n	background-color: #336;\r\n	color: #fff;\r\n}\r\n.vnav ul li a\r\n{\r\n	color: #336;\r\n	background-color: #cc9;\r\n}\r\n.vnav ul li a:hover\r\n{\r\n	background-color: #336;\r\n	color: #fff;\r\n}\r\n','0','This style is part of the design released into public domain:\r\nhttp://webhost.bridgew.edu/etribou/layouts/2col_footer/index.html','','1','','2006-06-01 00:00:00','.ruthcolors.css','admin','1010','blank','','text/css','users');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('/*---- Layout ----*/\r\ndiv\r\n{\r\n	/* this is for netscape 4\'s sake */\r\n	padding: 0;\r\n	margin: 0;\r\n}\r\nbody\r\n{\r\n	padding: 20px;\r\n	margin: 0;\r\n}\r\n#oldbrowser\r\n{\r\n	text-align: center;\r\n}\r\n#masthead h1\r\n{\r\n	display: inline;\r\n}\r\n#leftcolumn\r\n{\r\n	margin-right: -200px;	/* IE 4 fix */\r\n}\r\n#leftColumn\r\n{\r\n	float: left;\r\n	width: 200px;\r\n	/*/*/ /*/margin: 0 0 0 2px; /* Silly Netscape hack to get the borders to line up */\r\n}\r\n#contentColumn\r\n{\r\n	width: auto;\r\n	margin-left: 199px;\r\n}\r\n#masthead, #innerLeftColumn, #innerContentColumn, #innerFooter\r\n{\r\n	padding: 10px;\r\n}\r\n#footer\r\n{\r\n	clear: left;\r\n}\r\n#innerFooter\r\n{\r\n	text-align: center;\r\n}\r\n#innerContentColumn\r\n{\r\n	overflow: visible;\r\n	height: 100%;	/* fix the Win32 IE float bug */\r\n}\r\n#contentColumn>#innerContentColumn\r\n{\r\n	height: auto;	/* fix Opera 5 which breaks with the above IE fix */\r\n}\r\n#innercontentcolumn\r\n{\r\n	height: 100%;	/* fix IE 5.0 which parse the Opera fix, note the selector is all lower case */\r\n}\r\n#masthead, #footer\r\n{\r\n	z-index: 10;\r\n}\r\n\r\n/*---- Borders ----*/\r\n#pageFrame\r\n{\r\n	border: solid 1px #000;\r\n}\r\n#footer, #masthead, #innerLeftColumn, #contentColumn, .hnav\r\n{\r\n	border: solid 0px #000;\r\n}\r\n#footer\r\n{\r\n	border-top-width: 1px;\r\n}\r\n#masthead, .hnav\r\n{\r\n	border-bottom-width: 1px;\r\n}\r\n#innerLeftColumn\r\n{\r\n	border-right-width: 1px;\r\n}\r\n#contentColumn\r\n{\r\n	border-left-width: 1px;\r\n}\r\n\r\n/*---- Visual Elements ----*/\r\nbody\r\n{\r\n	background-color: #ddd;\r\n	color: #000;\r\n	padding: 20px;\r\n	margin: 0;\r\n}\r\n#pageFrame\r\n{\r\n	background-color: #fff;\r\n	color: #000;\r\n	min-width: 500px;\r\n}\r\n.hide\r\n{\r\n	display: none;\r\n}\r\n\r\n/*---- Simple Horizontal Navigation for IE 4/5.0/NS4 ----*/\r\n.hnav\r\n{\r\n	white-space: nowrap;\r\n	margin: 0;\r\n	color: #000;\r\n}\r\n.hnav\r\n{\r\n	padding: 3px 0 4px 0;\r\n}\r\n.hnav ul\r\n{\r\n	text-align: center;\r\n	list-style-type: none;\r\n	line-height: normal;\r\n	margin: 0;\r\n	padding: 0;\r\n}\r\n.hnav ul li\r\n{\r\n	display: inline;\r\n	white-space: nowrap;\r\n	/*/*/ /*/display: none;/*  */\r\n}\r\n','0','This style is part of the design released into public domain:\r\nhttp://webhost.bridgew.edu/etribou/layouts/2col_footer/index.html','','1','','2006-06-01 00:00:00','.ruthsimple.css','admin','1010','blank','','text/css','users');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('div#oldbrowser\r\n{\r\n	display: none;	/* hide the standards compliant warning - this isn\'t a catch all, but works for old browsers */\r\n}\r\na img\r\n{\r\n	border-width: 0;	/* remove the border around linked images. <img border=\"0\" ..../> is invalid in XHTML 1.1 */\r\n}\r\n#innerLeftColumn img\r\n{\r\n	display: block;\r\n	margin: 0 auto;\r\n	text-align: center;	/* IE 5 centering hack */\r\n}\r\n#innerLeftColumn, #innerContentColumn\r\n{\r\n	overflow: visible;	/* fix the Win32 IE italics bug */\r\n	height: 100%;		/* ditto */\r\n}\r\n#innerleftcolumn, #innercontentcolumn\r\n{\r\n	overflow: hidden;	/* IE5.0 fix the Win32 IE italics bug */\r\n	width: 100%;\r\n	height: auto;\r\n}\r\n#innerleftcolumn/* hide from IE5.0 */, #innercontentcolumn/* hide from IE5.0 */\r\n{\r\n	overflow: visible;	/* reset IE5.0 fix for 5.5 */\r\n	height: 100%;		/* ditto */\r\n	width: auto;		/* reset Safair after picking up the above IE5.0 fix : it\'s case insensitive to CSS selectors */\r\n}\r\n* html #masthead\r\n{\r\n	position: relative;\r\n}','0','This style is part of the design released into public domain:\r\nhttp://webhost.bridgew.edu/etribou/layouts/2col_footer/index.html','','1','','2006-06-01 00:00:00','.ruthcomplex.css','admin','1010','blank','','text/css','users');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('.vnav ul, .vnav ul li\r\n{\r\n	margin: 0;\r\n	padding: 0;\r\n	list-style-type: none;\r\n	display: block;\r\n}\r\n.vnav ul\r\n{\r\n	border: solid 1px #000;\r\n	border-bottom-width: 0;\r\n}\r\n.vnav ul li\r\n{\r\n	border-bottom: solid 1px #000;\r\n}\r\n.vnav ul li a\r\n{\r\n	display: block;\r\n	text-decoration: none;\r\n	padding: 2px 10px;\r\n}\r\n/* Hide from IE-mac */\r\n* html .vnav ul li a/* hide from IE 5.0 */ {height: 1%;}\r\n/* End hide */\r\n','0','This style is part of the design released into public domain:\r\nhttp://webhost.bridgew.edu/etribou/layouts/2col_footer/index.html','','1','','2006-06-01 00:00:00','.ruthcomplex_vnav.css','admin','1010','blank','','text/css','users');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('/*** Complex Horizontal Navigation ***/\r\n.hnav ul li\r\n{\r\n	margin: 0;\r\n}\r\n.hnav ul li a\r\n{\r\n	text-decoration: none;\r\n	color: #000;\r\n	background-color: #eee;\r\n	margin: 0 -1px 0 0;\r\n	padding: 3px 10px 4px 10px;\r\n	border-left: solid 1px #000;\r\n	border-right: solid 1px #000;\r\n}\r\n.hNAV\r\n{\r\n	padding: 0;		/*	IE5/Win will resize hnav to fit the heights of its\r\n						inline children that have vertical padding. So this\r\n						incorrect case selector will remove that padding */\r\n}\r\n.HNAV ul li a\r\n{\r\n	height: 1%;		/*	holly hack for IE5/Win because 5.0 doesn\'t recognize \r\n						inline padding by default */\r\n}\r\nhtml>body .HNAV\r\n{\r\n	padding: 3px 0 4px 0;	/* Fix for Safari which is case-insensitive */\r\n	height: auto;\r\n}\r\n.hnav ul li a:hover\r\n{\r\n	text-decoration: underline;\r\n}\r\n.hnav ul li a:hover\r\n{\r\n	text-decoration: none;\r\n}\r\n.hnav ul li span\r\n{\r\n	display: none;\r\n}\r\n\r\n\r\n','0','This style is part of the design released into public domain:\r\nhttp://webhost.bridgew.edu/etribou/layouts/2col_footer/index.html','','1','','2006-06-01 00:00:00','.ruthcomplex_hnav.css','admin','1010','blank','','text/css','users');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('<?SPINE_LogDump?>','0','This is where you put comments about this content','','1','','2006-06-01 00:00:00','stats.html','admin','1010','blank','STUB Content','text/plain','users');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('/*\r\n	NS4 doesn\'t do well with added colors because of the extra padding added inside each\r\n	block element. So colors are imported. This theme file will, thus, contain mostly\r\n	font stuff.\r\n */\r\n@import \".ruthcolors.css\";\r\n\r\n/*** Left Column ***/\r\n#innerLeftColumn p\r\n{\r\n	font-family: Georgia, \"Times New Roman\", serif;\r\n	font-size: 70%;\r\n	line-height: 140%;\r\n	/*/*/ /*/line-height: normal;/* NS4 Adds too much space, so reset the line-height */\r\n}\r\n#innerLeftColumn h3\r\n{\r\n	color: #006;\r\n	margin: 1em 0 0 0;\r\n}\r\n\r\n/*** Content ***/\r\n#innerContentColumn\r\n{\r\n	font-family: Georgia, \"Times New Roman\", serif;\r\n}\r\n#innerContentColumn h3\r\n{\r\n	color: #009;\r\n}\r\n\r\n/*** Masthead ***/\r\n#masthead h1\r\n{\r\n	font-family: Georgia, \"Times New Roman\", serif;\r\n}\r\n\r\n/*** Footer ***/\r\n#innerFooter\r\n{\r\n	font-family: Georgia, \"Times New Roman\", serif;\r\n}\r\n\r\n/*** Horizontal Navigation Area ***/\r\n.hnav\r\n{\r\n	font-family: verdana, helvetica, sans-serif;\r\n	font-weight: bold;\r\n	font-size: 70%;\r\n}\r\n\r\n/*** Vertical Navigation Area ***/\r\n.vnav\r\n{\r\n	font-family: verdana, helvetica, sans-serif;\r\n	font-weight: bold;\r\n	font-size: 70%;\r\n	line-height: 180%; /* Mozilla suffers from a rounding problem, \r\n						  this value may have to be tweaked if you \r\n						  change the font family or size \r\n						*/\r\n	/*/*/ /*/line-height: 120%;/* NS4 Adds too much space, so lower it */\r\n}\r\n','0','This CSS is part of the design released into public domain:\r\nhttp://webhost.bridgew.edu/etribou/layouts/2col_footer/index.html','','1','','2006-06-01 00:00:00','.ruththeme.css','admin','1010','blank','','text/css','users');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('SPINE includes 5 default styles:<br><center>\r\n<a href=\"/images/spine_style.jpg\"><img src=\"/images/spine_style.jpg\" width=\"513\" height=\"384\"></a><br>Default SPINE style<br><br>\r\n<a href=\"/images/spine2_style.jpg\"><img src=\"/images/spine2_style.jpg\" width=\"513\" height=\"384\"></a><br>Second SPINE style<br><br>\r\n<a href=\"/images/eidogreen_style.jpg\"><img src=\"/images/eidogreen_style.jpg\" width=\"513\" height=\"384\"></a><br>Eidogreen style, contributed by jhThorsen<br><br>\r\n<a href=\"/images/eidored_style.jpg\"><img src=\"/images/eidored_style.jpg\" width=\"513\" height=\"384\"></a><br>Eidored style, contributed by jhThorsen<br><br>\r\n<a href=\"/images/ruth_style.jpg\"><img src=\"/images/ruth_style.jpg\" width=\"513\" height=\"384\"></a><br>Public domain style, made available through <a href=\"http://webhost.bridgew.edu/etribou/layouts/2col_footer/index.html\">Ruthsarian Layouts</a></center>','0','This is where you put comments about this content','','1','','2006-06-01 00:00:00','styles.html','admin','1010','main','Included Styles','','users');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('<table width=\"1000\" border=\"0\" bgcolor=\"#ffffff\" cellpadding=\"0\" cellspacing=\"0\">\r\n<tr width=\"1000\">\r\n<form method=\"post\" action=\"<?SPINE_Location?>admin/navbar/save/\">\r\n\r\n<td colspan=\"5\">$error<b>Navigation Bar Edit : $filename</b><br>\r\n<input type=\"submit\" value=\"Save\" class=\"button\">\r\n<input type=\"button\" value=\"Advanced\" onclick=\"o=document.getElementById(\'advanced\'); if(o.style.display==\'inline\'){o.style.display=\'none\';}else{o.style.display=\'inline\';}\" class=\"button\">\r\n<br><b>Settings :</b></td></tr>\r\n<tr bgcolor=\"#ffffff\" >\r\n<td>\r\n<table width=\"100%\" border=\"0\" bgcolor=\"#ffffff\" cellpadding=\"0\" cellspacing=\"0\" id=\"advanced\" style=\"display:none\">\r\n<tr bgcolor=\"#ffffff\">\r\n<td width=\"20%\">Owner<sup>[<a class=\"info\" onClick=\"return false;\">?<span>This shows the navbar owner.</span></a>]</sup><br>$owner</td>\r\n<td width=\"20%\">Usergroup<sup>[<a class=\"info\" onClick=\"return false;\">?<span>This shows the navbar ownergroup.</span></a>]</sup><br><select name=\"usergroup\" class=\"general\">$usergroup</select></td>\r\n<td width=\"20%\">Group Permissions<sup>[<a class=\"info\" onClick=\"return false;\">?<span>These checkboxes set the navbar group access permissions.</span></a>]</sup><br>$gpermissions</td>\r\n<td width=\"20%\">Public Permissions<sup>[<a class=\"info\" onClick=\"return false;\">?<span>These checkboxes set the navbar public access permissions.</span></a>]</sup><br>$wpermissions</td>\r\n<td width=\"20%\">Modified<sup>[<a onClick=\"return false;\" class=\"info\">?<span>This is the last modifcation date of the navbar.</span></a>]</sup><br>$modified</td>\r\n</td></tr></table>\r\n</tr>\r\n<input type=\"hidden\" name=\"type\" value=\"$type\">\r\n<input type=\"hidden\" name=\"name\" value=\"$filename\">\r\n\r\n<tr width=\"80%\"  bgcolor=\"#ffffff\">\r\n<td colspan=\"5\">\r\n<input type=\"hidden\" name=\"name\" value=\"$filename\">\r\n<input type=\"hidden\" name=\"action\" value=\"settings\">\r\n<table border=\"0\" cellspacing=0 cellpadding=0><tr bgcolor=\"#ffffff\">\r\n<td>Alignment <sup>[<a onClick=\"return false;\" class=\"info\">?<span>This attribute defines the horizontal alignment.</span></a>]</sup>\r\n</b></td>\r\n<td>Positioning <sup>[<a onClick=\"return false;\" class=\"info\">?<span>This attribute defines the navigation bar positioning.</span></a>]</sup></td>\r\n<td>Font <sup>[<a onClick=\"return false;\" class=\"info\">?<span>This attribute defines the font face.</span></a>]</sup></td>\r\n<td>Size <sup>[<a onClick=\"return false;\" class=\"info\">?<span>This attribute defines the font size.</span></a>]</sup></td>\r\n<td>Color <sup>[<a onClick=\"return false;\" class=\"info\">?<span>this attribute defines the font color.</span></a>]</sup></td>\r\n<td>Style <sup>[<a onClick=\"return false;\" class=\"info\">?<span>This attribute defines the font style.</span></a>]</sup></td>\r\n<td>Separator  <sup>[<a onClick=\"return false;\" class=\"info\">?<span>This attribute defines the separator used to join each link in the navigation bar.</span></a>]</sup></td>\r\n\r\n</tr>\r\n\r\n<tr><td><select name=\"alignment\">$alignment</select>&nbsp;</td>\r\n<td><select name=\"positioning\">$positioning</select>&nbsp;</td>\r\n<td><input type=\"text\" size=\"9\" name=\"font\" value=\"$font\">&nbsp;</td>\r\n<td><input type=\"text\" size=\"3\" name=\"size\" class=\"input\" value=\"$size\">&nbsp;</td>\r\n<td><input type=\"text\" size=\"7\" name=\"color\" class=\"input\" value=\"$color\">&nbsp;</td>\r\n\r\n<td><select name=\"style\">$style</select>&nbsp;</td>\r\n<td><input type=\"text\" size=\"7\" name=\"separator\" class=\"input\" value=\"$separator\">&nbsp;</td>\r\n</form></tr></table>\r\n\r\n<hr width=\"95%\">\r\n\r\n<!-- Button List -->\r\n\r\n<table width=\"60%\" border=\"0\" bgcolor=\"#ffffff\" cellpadding=\"0\" cellspacing=\"0\">\r\n<tr>\r\n<td>URL <sup>[<a onClick=\"return false;\" class=\"info\">?<span>Define the content name or URL here.</span></a>]</sup></td>\r\n\r\n<td>Image <sup>[<a onClick=\"return false;\" class=\"info\">?<span>Define an optional image URL to create a clickable image.</span></a>]</sup></td>\r\n\r\n<td>Label <sup>[<a onClick=\"return false;\" class=\"info\">?<span>Define the link text here.</span></a>]</sup></td>\r\n\r\n\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</tr>\r\n\r\n<tr>\r\n<form method=\"post\" action=\"<?SPINE_Location?>admin/navbar/addbutton/\">\r\n<input type=\"hidden\" name=\"name\" value=\"$filename\">\r\n<input type=\"hidden\" name=\"id\" value=\"$navbar\">\r\n<input type=\"hidden\" name=\"position\" value=\"$position\">\r\n<td align=\"center\"><input type=\"text\" size=\"25\" name=\"link\" class=\"input\" value=\"\"></td>\r\n<td align=\"center\"><input type=\"text\" size=\"25\" name=\"image\" class=\"input\" value=\"\"></td>\r\n<td align=\"center\"><input type=\"text\" size=\"25\" name=\"label\" class=\"input\" value=\"\"></td>\r\n<td align=\"center\"><input type=\"image\" src=\"/images/save.png\" alt=\"Save\"></td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n<td>&nbsp;</td>\r\n</form>\r\n</tr>\r\n\r\n$buttonlist\r\n\r\n</table>\r\n\r\n</td></table></font>\r\n','0','','','1','','2006-06-01 00:00:00','.admin-navbar','admin','0000','.admin','Navigation Bar Administration','','admin');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('<pre>TODO\r\n------\r\n- POD\r\n- HOWTO\r\n- Overview Documentation\r\n- Quickstart (most needed functions)\r\n- Add documentation.. period\r\n\r\nTODO - Low Priority (random order)\r\n----------------------------------\r\n\r\n- Case insensitive match fix for mysql (foo != FOO.. silly germans) - Fix in dumper, mention in FAQ\r\n- Optimize ! http://dev.mysql.com/doc/mysql/en/Query_Speed.html\r\n\r\nDevelopment\r\n- Logging administration\r\n  - sorting\r\n  - link logging to log handler\r\n- Message administration\r\n  - Change Message structure to provide mail possibilities (extra fields - attributes)\r\n- Quickstart (most needed functions)\r\n  -> Documentation\r\n  -> Central admin page : \"edit index.html content\" \"edit main style\"\r\n- Limited versioning info (with patch??)\r\n  -> Part of Plugins (SPINE::Wiki)\r\n- Command Line Tool + Plugin : Work in progress\r\n- Virtual Folders (Plugin)\r\n\r\n- Add graphical counter example\r\n- Images directory (symlink/per setup or general?)\r\n- onchange in content (\"Changed\" - \"Unchanged\") <- javascript\r\n- Wiki Plugin - BETA\r\n\r\n</pre>','0','Things that are to do','document doc todo','0','','2006-06-01 00:00:00','todo.html','admin','0010','main','To Do','text/html','admin');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('<h2>Tips &amp; Tricks</h2>\r\n<ol><li><u>Macro values can also contain HTML tags, including SPINE Markers</u>\r\n<br>Example:\r\nDefine two Macro sets and include a Navbar as value:<br>\r\nDefine Macro set #1 (Old)<br>\r\nAdd a macro pair : name navbar ; value &lt;?SPINE_Navbar(\"old\")?&gt;<br>\r\nDefine Macro set #2 (New)<br>\r\nAdd a macro pair : name navbar ; value &lt;?SPINE_Navbar(\"new\")?&gt;\r\nIn the Style, use &lt;?SPINE_Macro(\"navbar\")?&gt;.<br>\r\nIn the content where you want to display the \'new\' Navbar, select \'New\' from the Macro list.<br>\r\nIn the content where you want to display the \'old\' Navbar, select \'Old\' from the Macro list.<br>\r\n</li>\r\n<li><u>Messages can be used for weblogs</u><br>\r\nSPINE does not include an official Weblog (blog, journal, ..) plugin but the Messages plugin can be used to do it.</li>\r\n<li><u>Dynamic Text Editor in the Administration</u><br>\r\nSPINE is not bundled with a dynamic text editor (WYSIWYG) but there is unofficial support. See <a href=\"tinymce.html\">TinyMCE Installation</a> page.</li>\r\n<li><u>AJAX support</u><br>\r\nNone of the plugins does AJAX support out of the box but it is quite simple to include it in your page. See the Messages AJAX example page.</li>\r\n<li><u>True files support</u><br>\r\nSPINE transparently handles displaying virtual files and true files. When a content is created but a true file with that name exists, SPINE will give precedence to that file.</li>\r\n  \r\n<li><u>Dynamic Media type</u><br>Content of any type can be generated\r\n  By setting a custom mime-type for a content, you can display virtually any data type. Make sure to use a style that Two included examples demonstrate this feature.\r\n  <ul><li>XML Changes shows XML data of the most recent changes, using a plugin. </li>\r\n  <li>Pie Chart demonstrates generating dynamic image data transparently.</li></ul>\r\n</li>\r\n </ol>','0','This is where you put comments about this content','tips tricks','1','','2006-06-01 00:00:00','tips.html','admin','1010','main','Tips & Tricks','','users');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('<table width=\"1000\" border=\"0\" bgcolor=\"#ffffff\" cellpadding=\"0\" cellspacing=\"0\">\r\n\r\n<tr width=\"1000\">\r\n<form method=\"post\" action=\"<?SPINE_Location?>admin/content/save/\">\r\n\r\n<td colspan=\"5\" width=\"1000\">\r\n$error<b>Content Edit : $filename</b><br>\r\n<input type=\"submit\" value=\"Save\" class=\"button\">\r\n<input type=\"button\" value=\"Preview\" onclick=\"window.open(\'<?SPINE_Location?>$filename\',\'Hint\',\'width=800,height=600,scrollbars=yes\'); return false;\" class=\"button\">\r\n<input type=\"button\" value=\"Advanced\" onclick=\"o=document.getElementById(\'advanced\'); if(o.style.display==\'inline\'){o.style.display=\'none\';}else{o.style.display=\'inline\';}\" class=\"button\">\r\n<input type=\"hidden\" name=\"name\" value=\"$filename\">\r\n</td></tr>\r\n\r\n<tr width=\"1000\">\r\n<td width=\"15%\">Size <sup>[<a onClick=\"return false;\" class=\"info\">?<span>This is the size of the content body.</span></a>]</sup> :</td><td width=\"25%\"> $size bytes</td>\r\n\r\n<td width=\"15%\">Last Modified <sup>[<a onClick=\"return false;\" class=\"info\">?<span>This is the last modifcation date of the content.</span></a>]</sup> : </td><td width=\"25%\">$lastmod</td>\r\n<td width=\"20%\">&nbsp;</td></tr>\r\n\r\n<tr width=\"1000\">\r\n<td width=\"15%\">\r\nTitle <sup>[<a class=\"info\" onClick=\"return false;\">?<span>The Title contains the string that can be used in conjunction with &lt;?SPINE_Title?&gt;.</span></a>]</sup> : </td><td width=\"25%\"><input type=\"text\" class=\"input\" name=\"title\" value=\"$title\" size=\"25\"></td>\r\n\r\n<td width=\"15%\">\r\nStyle <sup>[<a class=\"info\" onClick=\"return false;\">?<span>This is allows you to define a style.</span></a>]</sup> : </td><td width=\"25%\"><select name=\"style\" class=\"general\">\r\n<option>Select Style $stylelist</select></td>\r\n<td width=\"20%\">&nbsp;</td>\r\n</tr>\r\n\r\n<tr width=\"1000\">\r\n<td colspan=\"5\"  width=\"1000\">\r\n<table width=\"1000\" border=\"0\" bgcolor=\"#ffffff\" cellpadding=\"0\" cellspacing=\"0\" id=\"advanced\" style=\"display:none\">\r\n<tr width=\"1000\"><td width=\"1000\" colspan=\"5\"><img src=\"\" width=\"997\" height=\"1\"></td></tr>\r\n<tr width=\"1000\">\r\n<td width=\"20%\">Enter to &lt;br&gt; <sup>[<a class=\"info\" onClick=\"return false;\">?<span>This feature toggles converting newlines in the content to HTML breaks.</span></a>]</sup> : </td><td width=\"25%\">$breaks</td>\r\n<td width=\"20%\">\r\nLog Access <sup>[<a class=\"info\" onClick=\"return false;\">?<span>This feature toggles logging access to this Content.</span></a>]</sup> : </td><td width=\"25%\">$logging</td><td width=\"10%\">&nbsp;</td></tr>\r\n\r\n<tr bgcolor=\"#ffffff\" width=\"1000\"><td width=\"20%\">Macros <sup>[<a class=\"info\" onClick=\"return false;\">?<span>This is allows you to define Macros.</span></a>]</sup> :\r\n\r\n</td><td width=\"25%\"><select name=\"macros\" class=\"general\">\r\n<option value=\"\">none\r\n$macrolist\r\n</select></td>\r\n<td width=\"20%\">\r\nKeywords <sup>[<a class=\"info\" onClick=\"return false;\">?<span>This is allows you to define keywords for the search engine feature. Separate the words with spaces.</span></a>]</sup> : </td><td width=\"25%\">\r\n<input type=\"text\" name=\"keywords\" value=\"$keywords\" size=\"30\" class=\"input\"></td><td width=\"10%\">&nbsp;</td></tr>\r\n\r\n<tr bgcolor=\"#ffffff\" width=\"1000\">\r\n<td width=\"15%\">\r\nOwner <sup>[<a class=\"info\" onClick=\"return false;\">?<span>This shows the content owner.</span></a>]</sup> :</td><td width=\"25%\">$owner</td>\r\n\r\n<td width=\"15%\">Group <sup>[<a class=\"info\" onClick=\"return false;\">?<span>This shows the content ownergroup.</span></a>]</sup> : </td><td width=\"25%\"><select name=\"usergroup\" class=\"general\">$group</select></td><td width=\"20%\">&nbsp;</td></tr>\r\n\r\n<tr bgcolor=\"#ffffff\" width=\"1000\">\r\n\r\n<td width=\"15%\" valign=\"top\">\r\nPermissions <sup>[<a class=\"info\" onClick=\"return false;\">?<span>These checkboxes set the content access permissions.</span></a>]</sup> :</td>\r\n\r\n<td width=\"25%\">Group: $gpermissions<br>Public&nbsp;: $wpermissions</td>\r\n<td width=\"15%\" valign=\"top\">Content-type <sup>[<a class=\"info\" onClick=\"return false;\">?<span>This attribute defines the content media type.</span></a>]</sup> : </td><td width=\"25%\" valign=\"top\"><input type=\"text\" class=\"input\" name=\"type\" value=\"$type\" size=\"25\"></td>\r\n<td width=\"20%\">&nbsp;</td>\r\n</tr>\r\n\r\n<tr bgcolor=\"#ffffff\" width=\"1000\"><td colspan=\"5\" width=\"100%\">\r\nComment <sup>[<a class=\"info\" onClick=\"return false;\">?<span>Define comments about this content here.\r\n</span></a>]</sup> :<br>\r\n\r\n<textarea cols=\"80\" rows=\"2\" name=\"icomment\" class=\"input\">$icomment</textarea>\r\n</td></tr>\r\n</table>\r\n\r\n</td></tr>\r\n\r\n<tr width=\"1000\"><td colspan=\"4\">\r\nContents <sup>[<a class=\"info\" onClick=\"return false;\">?<span>This is the actual Content body.</span></a>]</sup> :\r\n<br><textarea cols=\"110\" rows=\"18\" name=\"body\" class=\"input\">$body</textarea></td>\r\n<td>&nbsp;</td>\r\n</table>','0','I\'m not sure what this is...','','1','','2006-06-01 00:00:00','.admin-content','admin','0000','.admin','Content Administration','','admin');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('<script language=\"javascript\" type=\"text/javascript\" src=\"../jscripts/tiny_mce/tiny_mce.js\"></script>\r\n<script language=\"javascript\" type=\"text/javascript\" src=\"../jscripts/tiny_mce/init.js\"></script>\r\n\r\n<table width=\"1000\" border=\"0\" bgcolor=\"#ffffff\" cellpadding=\"0\" cellspacing=\"0\">\r\n\r\n<tr width=\"1000\">\r\n<form method=\"post\" action=\"<?SPINE_Location?>admin/content/save/\">\r\n\r\n<td colspan=\"5\" width=\"1000\">\r\n$error<b>Content Edit : $filename</b><br>\r\n<input type=\"submit\" value=\"Save\" class=\"button\">\r\n<input type=\"button\" value=\"Preview\" onclick=\"window.open(\'<?SPINE_Location?>$filename\',\'Hint\',\'width=800,height=600,scrollbars=yes\'); return false;\" class=\"button\">\r\n<input type=\"button\" value=\"Advanced\" onclick=\"o=document.getElementById(\'advanced\'); if(o.style.display==\'inline\'){o.style.display=\'none\';}else{o.style.display=\'inline\';}\" class=\"button\">\r\n<input type=\"hidden\" name=\"name\" value=\"$filename\">\r\n</td></tr>\r\n\r\n<tr width=\"1000\">\r\n<td width=\"15%\">Size [<a class=\"info\" href=\"#\">?<span>This is the size of the content body.</span></a>] :</td><td width=\"25%\"> $size bytes</td>\r\n\r\n<td width=\"15%\">Last Modified [<a class=\"info\" href=\"#\">?<span>This is the last modifcation date of the content.</span></a>] : </td><td width=\"25%\">$lastmod</td>\r\n<td width=\"20%\">&nbsp;</td></tr>\r\n\r\n<tr width=\"1000\">\r\n<td width=\"15%\">\r\nTitle [<a class=\"info\" href=\"#\">?<span>The Title contains the string that can be used in conjunction with &lt;?SPINE_Title?&gt;.</span></a>] : </td><td width=\"25%\"><input type=\"text\" class=\"input\" name=\"title\" value=\"$title\" size=\"25\"></td>\r\n\r\n<td width=\"15%\">\r\nStyle [<a class=\"info\" href=\"#\">?<span>This is allows you to define a style.</span></a>] : </td><td width=\"25%\"><select name=\"style\" class=\"general\">\r\n<option>Select Style $stylelist</select></td>\r\n<td width=\"20%\">&nbsp;</td>\r\n</tr>\r\n\r\n<tr width=\"1000\">\r\n<td colspan=\"5\"  width=\"1000\">\r\n<table width=\"1000\" border=\"0\" bgcolor=\"#ffffff\" cellpadding=\"0\" cellspacing=\"0\" id=\"advanced\" style=\"display:none\">\r\n<tr width=\"1000\"><td width=\"1000\" colspan=\"5\"><img src=\"\" width=\"997\" height=\"1\"></td></tr>\r\n<tr width=\"1000\">\r\n<td width=\"20%\">Enter to &lt;br&gt; [<a class=\"info\" href=\"#\">?<span>This feature toggles converting newlines in the content to HTML breaks.</span></a>] : </td><td width=\"25%\">$breaks</td>\r\n<td width=\"20%\">\r\nLog Access [<a class=\"info\" href=\"#\">?<span>This feature toggles logging access to this Content.</span></a>] : </td><td width=\"25%\">$logging</td><td width=\"10%\">&nbsp;</td></tr>\r\n\r\n<tr bgcolor=\"#ffffff\" width=\"1000\"><td width=\"20%\">Macros [<a class=\"info\" href=\"#\">?<span>This is allows you to define Macros.</span></a>] :\r\n\r\n</td><td width=\"25%\"><select name=\"macros\" class=\"general\">\r\n<option value=\"\">none\r\n$macrolist\r\n</select></td>\r\n<td width=\"20%\">\r\nKeywords [<a class=\"info\" href=\"#\">?<span>This is allows you to define keywords for the search engine feature. Separate the words with spaces.</span></a>] : </td><td width=\"25%\">\r\n<input type=\"text\" name=\"keywords\" value=\"$keywords\" size=\"30\" class=\"input\"></td><td width=\"10%\">&nbsp;</td></tr>\r\n\r\n<tr bgcolor=\"#ffffff\" width=\"1000\">\r\n<td width=\"15%\">\r\nOwner [<a class=\"info\" href=\"#\">?<span>This shows the content owner.</span></a>] :</td><td width=\"25%\">$owner</td>\r\n\r\n<td width=\"15%\">Group [<a class=\"info\" href=\"#\">?<span>This shows the content ownergroup.</span></a>] : </td><td width=\"25%\"><select name=\"usergroup\" class=\"general\">$group</select></td><td width=\"20%\">&nbsp;</td></tr>\r\n\r\n<tr bgcolor=\"#ffffff\" width=\"1000\">\r\n\r\n<td width=\"15%\" valign=\"top\">\r\nPermissions [<a class=\"info\" href=\"#\">?<span>These checkboxes set the content access permissions.\r\n</span></a>] :</td>\r\n\r\n<td width=\"25%\">Group: $gpermissions<br>Public&nbsp;: $wpermissions</td>\r\n<td width=\"15%\" valign=\"top\">Content-type [<a class=\"info\" href=\"#\">?<span>This attribute defines the content media type.</span></a>] : </td><td width=\"25%\" valign=\"top\"><input type=\"text\" class=\"input\" name=\"type\" value=\"$type\" size=\"25\"></td>\r\n<td width=\"20%\">&nbsp;</td>\r\n</tr>\r\n\r\n<tr bgcolor=\"#ffffff\" width=\"1000\"><td colspan=\"5\" width=\"100%\">\r\nComment [<a class=\"info\" href=\"#\">?<span>Define comments about this content here.\r\n</span></a>] :<br>\r\n\r\n<textarea cols=\"80\" rows=\"2\" name=\"icomment\" class=\"input\">$icomment</textarea>\r\n</td></tr>\r\n</table>\r\n\r\n</td></tr>\r\n\r\n<tr width=\"1000\"><td colspan=\"4\">\r\nContents [<a class=\"info\" href=\"#\">?<span>This is the actual Content body.</span></a>] :\r\n<br><textarea cols=\"110\" rows=\"18\" name=\"body\" class=\"input\">$body</textarea></td>\r\n<td>&nbsp;</td>\r\n</table>','0','I\'m not sure what this is...','','1','','2006-06-01 00:00:00','.admin-content.tinymce','admin','0000','.admin','Content Administration','','admin');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('body, div, td, a, input, textarea {\r\n	font-size: 10pt;\r\n	line-height: 15pt;\r\n	letter-spacing: 1px;\r\n	font-weight: normal;\r\n	font-family: sans-serif, arial;\r\n	color: #000000;\r\n}\r\n\r\nbody {\r\n	background: #ffffff url(/images/feath.jpg) repeat-y;\r\n	background-position: 50% 20px;\r\n	margin: 0;\r\n	padding: 0;\r\n	padding-bottom: 3em;\r\n}\r\n\r\ndiv#body a {\r\n	text-decoration: none;\r\n	font-weight: bold;\r\n	color: #990000;\r\n	border: 1px solid #ffffff;\r\n	padding: 2pt 1pt;\r\n	position: relative;\r\n}\r\n\r\ndiv#body a:hover {\r\n	border: 1px solid #000000;\r\n	background: #eeeeee;\r\n	z-index: 8;\r\n}\r\n\r\np, dl, ol {\r\n	margin: 2pt;\r\n	padding: 0pt;\r\n}\r\n\r\nol {\r\n	margin-left: 15pt;\r\n}\r\n\r\nh1, h2, h3 {\r\n	padding: 0;\r\n	margin: 0;\r\n}\r\n\r\nh1, h2 {\r\n	font-size: 16pt;\r\n	padding-top: 16pt;\r\n	padding-bottom: 6pt;\r\n	border-bottom: 2px solid #000000;\r\n}\r\n\r\nh2 {\r\n	font-size: 14pt;\r\n}\r\n\r\ndiv#headtxt h1 {\r\n	font-size: 18pt;\r\n	padding-top: 150px;\r\n	padding-bottom: 10pt;\r\n	max-width: 500pt;\r\n	border-bottom: 2px solid #000000;\r\n}\r\n\r\nh3 {\r\n	font-size: 13pt;\r\n	padding-top: 5pt;\r\n	padding-bottom: 4pt;\r\n}\r\n\r\ndiv#headbgrepeat {\r\n	background: url(/images/bgred.jpg) repeat-x top left;\r\n	background-position: 0 38px;\r\n}\r\n\r\ndiv#headbg {\r\n	background: url(/images/head.jpg) no-repeat top left;\r\n	font-weight: bold;\r\n}\r\n\r\ndiv#headtxt {\r\n	padding-left: 120px;\r\n	padding-right: 3em;\r\n	height: 347px;\r\n}\r\n\r\ndiv#headtxt a {\r\n	color: #000000;\r\n	padding: 0 4pt;\r\n	border: 0;\r\n	text-decoration: none;\r\n	font-weight: bold;\r\n	padding-bottom: 2.5em;\r\n	letter-spacing: -1px;\r\n}\r\n\r\ndiv#headtxt a:hover {\r\n	background: transparent;\r\n	color: #e0e0e0;\r\n	border-color: #770000;\r\n	border-bottom: 2px solid #770000;\r\n}\r\n\r\n#body {\r\n	padding-left: 120px;\r\n	padding-right: 3em;\r\n	max-width: 500pt;\r\n}\r\n\r\n#content {\r\n	border-bottom: 4px solid #990000;\r\n}\r\n\r\n#footer {\r\n	position: relative;\r\n	top: 16pt;\r\n	font-weight: bold;\r\n}\r\ndiv#footer a, div#footer a:hover {\r\n	border: 0;\r\n	color: #000000;\r\n	background: transparent\r\n}','0','CSS for eidored style','eidolon eidored css','1','','2006-06-01 00:00:00','eidored.css','admin','1010','blank','STUB Content','text/css','users');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('body, html {\r\n	height: 100%;\r\n}\r\n\r\nbody, td, div {\r\n	font-family: ans-serif, arial;\r\n	font-size: 11pt;\r\n	line-height: 15pt;\r\n}\r\n\r\nbody {\r\n	background: #99aaff;\r\n	margin: 0;\r\n	margin-left: 150pt;\r\n}\r\n\r\na {\r\n	color: #000000;\r\n	text-decoration: none;\r\n}\r\n\r\nh1 {\r\n	background: #99aaff;\r\n	font-size: 10pt;\r\n	font-weight: bold;\r\n	letter-spacing: 4px;\r\n	margin: 0;\r\n	padding: 0 14pt;\r\n	padding-top: 1em;\r\n	position: absolute;\r\n	top: 0;\r\n	left: 140pt;\r\n	text-align: center;\r\n	border-bottom: 2px dotted #000000;\r\n	border-right: 2px dotted #000000;\r\n}\r\n\r\n#container {\r\n	background: #acbb66 url(/images/bg.jpg) repeat-y;\r\n	background-position: 99% -30px;\r\n	height: 100%;\r\n	border-left: 2px dotted #000000;\r\n}\r\n\r\nbody>#container {\r\n	height: auto;\r\n	min-height: 100%;\r\n}\r\n\r\n#content {\r\n	padding: 3em;\r\n	padding-left: 2em;\r\n	padding-right: 200px;\r\n}\r\n\r\n#menu {\r\n	position: absolute;\r\n	left: 2em;\r\n	top: 3.5em;\r\n	width: 110pt;\r\n}\r\n\r\n#menu a {\r\n	padding: 0.2em;\r\n	margin: 0.1em;\r\n	display: block;\r\n	color: #ffffff;\r\n	border: 1px solid #99aaff;\r\n}\r\n\r\n#menu a:hover {\r\n	border-color: #000000;\r\n	color: #000000;\r\n}\r\n\r\n#menu h2 {\r\n	font-weight: bold;\r\n	font-size: 14pt;\r\n	margin: 0.3em;\r\n	margin-bottom: 0.6em;\r\n	color: #ffffff;\r\n}\r\n\r\n#content h2 {\r\n	font-weight: bold;\r\n	font-size: 14pt;\r\n	margin: 0.5em;\r\n	margin-left: -1em;\r\n	margin-right: -2em;\r\n	padding: 0.1em;\r\n	padding-left: 1em;\r\n	background: #556644;\r\n	color: #ffffff;\r\n}','0','This CSS is part of the style contributed by jhtorsen (jhth@online.no).','','1','','2006-06-01 00:00:00','.eidogreen.css','admin','1010','blank','STUB Content','text/css','users');
