insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('body {font-family:arial;font-size:12px;cursor:default;color:AAAAAA}\r\ntd {font-family:arial;font-size:12px;cursor:default;color:AAAAAAA}\r\na {color:FFBA27;text-decoration:none;font-size:16px}\r\na:hover {color:FFBA27;text-decoration:underline}\r\ninput {border-width:1;font-family:arial;font-size:12px;padding:2px;height:21px;border-color:CCCCCC;border-style:solid;background-color:F9F9F9;color:999999}','0','This is where you put comments about this content','','1','','2006-06-01 00:00:00','autonomous.css','admin','1010','blank','','text/css','users');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('<table width=\"1000\" border=\"0\" bgcolor=\"#ffffff\" cellpadding=\"0\" cellspacing=\"0\">\r\n\r\n<tr width=\"1000\">\r\n<form method=\"post\" action=\"<?SPINE_Location?>admin/wiki/save/\">\r\n\r\n<td colspan=\"5\" width=\"1000\">\r\n$error<b>Wiki Edit : $name</b><br>\r\n<input type=\"submit\" value=\"Save\" class=\"button\">\r\n<input type=\"button\" value=\"Advanced\" onclick=\"o=document.getElementById(\'advanced\'); if(o.style.display==\'inline\'){o.style.display=\'none\';}else{o.style.display=\'inline\';}\" class=\"button\">\r\n<input type=\"hidden\" name=\"name\" value=\"$filename\">\r\n</td></tr>\r\n\r\n<tr width=\"1000\">\r\n\r\n<td width=\"15%\">Last Modified <sup>[<a onClick=\"return false;\" class=\"info\">?<span>This is the last modifcation date of the wiki.</span></a>]</sup> : </td><td width=\"25%\">$lastmod</td>\r\n<td width=\"20%\">&nbsp;</td></tr>\r\n\r\n<tr width=\"1000\">\r\n<td width=\"15%\">\r\nOwner <sup>[<a class=\"info\" onClick=\"return false;\">?<span>This shows the content owner.</span></a>]</sup> :</td><td width=\"25%\">$owner</td>\r\n\r\n<td width=\"15%\">Group <sup>[<a class=\"info\" onClick=\"return false;\">?<span>This shows the content ownergroup.</span></a>]</sup> : </td><td width=\"25%\"><select name=\"usergroup\" class=\"general\">$group</select></td><td width=\"20%\">&nbsp;</td></tr>\r\n\r\n<tr bgcolor=\"#ffffff\" width=\"1000\">\r\n\r\n<td width=\"15%\" valign=\"top\">\r\nPermissions <sup>[<a class=\"info\" onClick=\"return false;\">?<span>These checkboxes set the content access permissions.</span></a>]</sup> :</td>\r\n\r\n<td width=\"25%\">Group: $gpermissions</td>\r\n<td width=\"25%\">Public&nbsp;: $wpermissions</td>\r\n<td width=\"20%\">&nbsp;</td>\r\n</tr>\r\n\r\n<tr width=\"1000\"><td colspan=\"4\">\r\n<sup>[<a class=\"info\" onClick=\"return false;\">?<span>This is the actual Wiki body.</span></a>]</sup> :\r\n<br><textarea cols=\"110\" rows=\"18\" name=\"body\" class=\"input\">$body</textarea></td>\r\n<td>&nbsp;</td>\r\n</table>','0','I\'m not sure what this is...','','1','','2006-06-01 00:00:00','.admin-wiki','admin','0000','.admin','Wiki Administration','','admin');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('<table width=\"1000\" border=\"0\" bgcolor=\"#ffffff\" cellpadding=\"0\" cellspacing=\"0\">\r\n\r\n<tr width=\"1000\">\r\n<form method=\"post\" action=\"<?SPINE_Location?>admin/content/save/\">\r\n\r\n<td colspan=\"5\" width=\"1000\">\r\n$error<b>Content Aanpassen : $filename</b><br>\r\n<input type=\"submit\" value=\"Opslaan\" class=\"button\">\r\n<input type=\"button\" value=\"Voorbeeld\" onclick=\"window.open(\'<?SPINE_Location?>$filename\',\'Hint\',\'width=800,height=600,scrollbars=yes\'); return false;\" class=\"button\">\r\n<input type=\"button\" value=\"Extras\" onclick=\"o=document.getElementById(\'advanced\'); if(o.style.display==\'inline\'){o.style.display=\'none\';}else{o.style.display=\'inline\';}\" class=\"button\">\r\n<input type=\"hidden\" name=\"name\" value=\"$filename\">\r\n</td></tr>\r\n\r\n<tr width=\"1000\">\r\n<td width=\"15%\">Bestandsgrootte <sup>[<a onClick=\"return false;\" class=\"info\">?<span>Dit is bestandsgrootte in bytes van de Content inhoud.</span></a>]</sup> :</td><td width=\"25%\"> $size bytes</td>\r\n\r\n<td width=\"15%\">Laatst aangepast op<sup>[<a onClick=\"return false;\" class=\"info\">?<span>Dit is de datum waarop deze Content het laatst is aangepast.</span></a>]</sup> : </td><td width=\"25%\">$lastmod</td>\r\n<td width=\"20%\">&nbsp;</td></tr>\r\n\r\n<tr width=\"1000\">\r\n<td width=\"15%\">\r\nTitel <sup>[<a class=\"info\" onClick=\"return false;\">?<span>Dit is de titel van de Content. Deze waarde kan worden gebruikt in &lt;?SPINE_Title?&gt;.</span></a>]</sup> : </td><td width=\"25%\"><input type=\"text\" class=\"input\" name=\"title\" value=\"$title\" size=\"25\"></td>\r\n\r\n<td width=\"15%\">\r\nStyle <sup>[<a class=\"info\" onClick=\"return false;\">?<span>Hier bepaal je de Style</span></a>]</sup> : </td><td width=\"25%\"><select name=\"style\" class=\"general\">\r\n<option>Select Style $stylelist</select></td>\r\n<td width=\"20%\">&nbsp;</td>\r\n</tr>\r\n\r\n<tr width=\"1000\">\r\n<td colspan=\"5\"  width=\"1000\">\r\n<table width=\"1000\" border=\"0\" bgcolor=\"#ffffff\" cellpadding=\"0\" cellspacing=\"0\" id=\"advanced\" style=\"display:none\">\r\n<tr width=\"1000\"><td width=\"1000\" colspan=\"5\"><img src=\"\" width=\"997\" height=\"1\"></td></tr>\r\n<tr width=\"1000\">\r\n<td width=\"20%\">Enter omzetten naar &lt;br&gt; <sup>[<a class=\"info\" onClick=\"return false;\">?<span>Converteer een enter naar een HTML break.</span></a>]</sup> : </td><td width=\"25%\">$breaks</td>\r\n<td width=\"20%\">\r\nToegang Loggen <sup>[<a class=\"info\" onClick=\"return false;\">?<span>Elke toegang tot deze Content </span></a>]</sup> : </td><td width=\"25%\">$logging</td><td width=\"10%\">&nbsp;</td></tr>\r\n\r\n<tr bgcolor=\"#ffffff\" width=\"1000\"><td width=\"20%\">Macros <sup>[<a class=\"info\" onClick=\"return false;\">?<span>Hier bepaal je de Macros.</span></a>]</sup> :\r\n\r\n</td><td width=\"25%\"><select name=\"macros\" class=\"general\">\r\n<option value=\"\">none\r\n$macrolist\r\n</select></td>\r\n<td width=\"20%\">\r\nSleutelwoorden <sup>[<a class=\"info\" onClick=\"return false;\">?<span>Hier bepaal je de sleutelwoorden die gebruikt worden in de Search Engine. Splits verschillende sleutelwoorden op met spaties.</span></a>]</sup> : </td><td width=\"25%\">\r\n<input type=\"text\" name=\"keywords\" value=\"$keywords\" size=\"30\" class=\"input\"></td><td width=\"10%\">&nbsp;</td></tr>\r\n\r\n<tr bgcolor=\"#ffffff\" width=\"1000\">\r\n<td width=\"15%\">\r\nEigenaar <sup>[<a class=\"info\" onClick=\"return false;\">?<span>Hier bepaal je de gebruiker die eigenaar is van deze Content.</span></a>]</sup> :</td><td width=\"25%\">$owner</td>\r\n\r\n<td width=\"15%\">Gebruikersgroep <sup>[<a class=\"info\" onClick=\"return false;\">?<span>Hier bepaal je welke gebruikersgroep toegang heeft tot deze Content.</span></a>]</sup> : </td><td width=\"25%\"><select name=\"usergroup\" class=\"general\">$group</select></td><td width=\"20%\">&nbsp;</td></tr>\r\n\r\n<tr bgcolor=\"#ffffff\" width=\"1000\">\r\n\r\n<td width=\"15%\" valign=\"top\">\r\nToegangsrechten <sup>[<a class=\"info\" onClick=\"return false;\">?<span>Deze aankruisvakjes bepalen de toegang tot deze Content.</span></a>]</sup> :</td>\r\n\r\n<td width=\"25%\">Gebruikersgroep: $gpermissions<br>Publiek&nbsp;: $wpermissions</td>\r\n<td width=\"15%\" valign=\"top\">Content-type <sup>[<a class=\"info\" onClick=\"return false;\">?<span>Hier bepaal je welk type data de Content bevat.</span></a>]</sup> : </td><td width=\"25%\" valign=\"top\"><input type=\"text\" class=\"input\" name=\"type\" value=\"$type\" size=\"25\"></td>\r\n<td width=\"20%\">&nbsp;</td>\r\n</tr>\r\n\r\n<tr bgcolor=\"#ffffff\" width=\"1000\"><td colspan=\"5\" width=\"100%\">\r\nCommentaar <sup>[<a class=\"info\" onClick=\"return false;\">?<span>Plaats hier extra informatie over je Content.\r\n</span></a>]</sup> :<br>\r\n\r\n<textarea cols=\"80\" rows=\"2\" name=\"icomment\" class=\"input\">$icomment</textarea>\r\n</td></tr>\r\n</table>\r\n\r\n</td></tr>\r\n\r\n<tr width=\"1000\"><td colspan=\"4\">\r\nInhoud <sup>[<a class=\"info\" onClick=\"return false;\">?<span>Bepaal hier de feitelijke</span></a>]</sup> :\r\n<br><textarea cols=\"110\" rows=\"18\" name=\"body\" class=\"input\">$body</textarea></td>\r\n<td>&nbsp;</td>\r\n</table>','0','I\'m not sure what this is...','','1','','2006-06-01 00:00:00','.admin-content.nl','admin','0000','.admin','Content Administration','','admin');
