insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('/* andreas01 - an open source xhtml/css website layout by Andreas Viklund (http://andreasviklund.com). Made for OSWD.org, free to use as-is for any purpose as long as the proper credits are given for the original design work. For design assistance and support, contact me through my website or through http://oswd.org/email.phtml?user=Andreas\r\n\r\nVersion: 1.0\r\n(July 25, 2005)\r\n\r\nScreen layout: */\r\n\r\nbody {\r\nmargin: 0 auto;\r\npadding: 0;\r\nfont: 76% Verdana,Tahoma,Arial,sans-serif;\r\nbackground: #f4f4f4 url(/images/andreas01bg.gif) top center repeat-y;\r\n}\r\n\r\n#wrap {\r\nbackground: #ffffff;\r\ncolor: #303030;\r\nmargin: 0 auto;\r\nwidth: 760px;\r\n}\r\n\r\n#header {\r\nclear: both;\r\nmargin: 20px 0 0 0;\r\npadding: 0;\r\nheight: 45px;\r\n}\r\n\r\n#header h1 {\r\nwidth: 250px;\r\nmargin: 0 0 10px 0;\r\nfloat: left;\r\n}\r\n\r\n#header p {\r\nwidth: 500px;\r\nfloat: right;\r\ntext-align: center;\r\ncolor: #a0a0a0;\r\nmargin: 0 0 10px 0;\r\nfont-size: 0.8em;\r\nline-height: 1.2em;\r\n}\r\n\r\n#frontphoto {\r\nmargin: 0 0 10px 0;\r\nborder: 0;\r\n}\r\n\r\n#avmenu {\r\nclear: left;\r\nfloat: left;\r\nwidth: 150px;\r\nmargin: 0 0 10px 0;\r\npadding: 0;\r\nfont-size: 0.9em;\r\n}\r\n\r\n#avmenu ul {	\r\nlist-style: none;\r\nwidth: 150px;\r\nmargin: 0 0 20px 0;\r\npadding: 0;\r\nfont-size: 1.1em;\r\n}	\r\n\r\n#avmenu li {\r\nmargin-bottom: 4px;\r\n}\r\n\r\n#avmenu li a {\r\nfont-weight: bold;\r\nheight: 20px;\r\ntext-decoration: none;\r\ncolor: #505050;\r\ndisplay: block;\r\npadding: 6px 0 0 10px;\r\nbackground: #f4f4f4;\r\nborder-left: 4px solid #cccccc;\r\n}	\r\n	\r\n#avmenu li a:hover {\r\nbackground: #eaeaea;\r\ncolor: #286ea0;\r\nborder-left: 4px solid #286ea0;\r\n}\r\n\r\n.announce {\r\nmargin: 10px 0 10px 0;\r\npadding: 10px;\r\nwidth: 130px;\r\ncolor: #505050;\r\nbackground-color: #f4f4f4;\r\nline-height: 1.3em;\r\n}\r\n\r\n#extras {\r\nfloat: right;\r\nwidth: 100px;\r\nmargin: 0 0 10px 0;\r\npadding: 0;\r\nfont-size: 0.9em;\r\nline-height: 1.5em;\r\n}\r\n\r\n#extras p {\r\nmargin: 0 0 1.5em 0;\r\n}\r\n\r\n#content {\r\nmargin: 0 110px 20px 160px;\r\nborder-left: 1px solid #f0f0f0;\r\nborder-right: 1px solid #f0f0f0;\r\npadding: 0 10px 0 10px;\r\nline-height: 1.6em;\r\ntext-align: left;\r\n}\r\n\r\n#content h2 {\r\nfont-size: 1.5em;\r\nmargin: 0 0 0.5em 0;\r\n}\r\n\r\n#content img {\r\npadding: 1px;\r\ndisplay:inline;\r\nbackground: #cccccc;\r\nborder: 4px solid #f0f0f0;\r\n}\r\n\r\nh3 {\r\nfont-size: 1.3em;\r\nmargin: 0 0 10px 0;\r\n}\r\n\r\na {\r\ntext-decoration: none;\r\ncolor: #286ea0;\r\n}\r\n\r\na:hover {\r\ntext-decoration: underline;\r\ncolor: #286ea0;\r\n}\r\n\r\na img {\r\nborder: 0;\r\n}\r\n\r\n#footer {\r\nclear: both;\r\nmargin: 0 auto;\r\npadding: 10px 0 20px 0;\r\nborder-top: 4px solid #f0f0f0;\r\nwidth: 760px;\r\ntext-align: center;\r\ncolor: #808080;\r\nfont-size: 0.9em;\r\n}\r\n\r\n#footer a {\r\ncolor: #808080;\r\ntext-decoration: none;\r\n}\r\n\r\n#footer a:hover {\r\ntext-decoration: underline;\r\n}\r\n\r\n.left {\r\nmargin: 10px 10px 5px 0;\r\nfloat: left;\r\n}\r\n\r\n.right {\r\nmargin: 10px 0 5px 10px;\r\nfloat: right;\r\n}\r\n\r\n.textright {\r\ntext-align: right;\r\n}\r\n\r\n.center {\r\ntext-align: center;\r\n}\r\n\r\n.small {\r\nfont-size: 0.8em;\r\n}\r\n\r\n.bold {\r\nfont-weight: bold;\r\n}\r\n\r\n.hide {\r\ndisplay: none;\r\n}','0','This is where you put comments about this content','','1','','2006-06-01 00:00:00','andreas01.css','admin','1010','blank','STUB Content','text/css','users');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('<h3 class=\"news\"><?SPINE_Message_Subject?></h3>\r\n<div class=\"newsdate\"><?SPINE_Message_Date?></div>\r\n<?SPINE_Message_Body?>\r\n','0','This is how a message will look like','','0','','2006-06-01 00:00:00','andreas_spinenews','admin','0010','main','','','admin');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('/* CSS Document */\r\n\r\nbody {\r\n	margin:0;\r\n	font-family:\"Trebuchet MS\", Arial, Verdana;\r\n	font-size:0.74em;\r\n	color:#555555;\r\n	background-color:#FFFFFF;\r\n	text-align:left;\r\n}\r\n\r\ninput {\r\n	background-color:#FFFFFF;\r\n	padding:3px;\r\n	font-family:Verdana, Arial, Helvetica, sans-serif;\r\n	font-size:10px;\r\n	color:#ffffff;\r\n	border:1px solid #728c40;\r\n	background-color:#94ae62;\r\n	margin-bottom:2px;\r\n}\r\ninput[type=submit] {\r\n	background-color:#FFFFFF;\r\n	padding:3px;\r\n	font-family:Verdana, Arial, Helvetica, sans-serif;\r\n	font-size:10px;\r\n	color:#666666;\r\n	border:1px solid #728c40;\r\n	margin-bottom:2px;\r\n}\r\n\r\na {\r\n	color:#4D87CE;\r\n	font-weight:bold;\r\n	text-decoration:none;\r\n}\r\na:hover {\r\n	color:#2a65ad;\r\n}\r\n\r\nol,ul {\r\n\r\n}\r\n\r\nli {\r\n	list-style-image:url(/images/plainli.gif);\r\n	line-height:20px;\r\n	text-align:left;\r\n}\r\n\r\n#menu {\r\n	width:100%;\r\n	padding-top:20px;\r\n	padding-bottom:20px;\r\n	background-color:#94AE62;\r\n	margin-bottom:50px;\r\n	color:#FFFFFF;\r\n	text-align:center;\r\n	font-family:Verdana, Arial, Helvetica, sans-serif;\r\n	font-size:11px;\r\n	font-variant:small-caps;\r\n	background-image:url(/images/plainmenu.jpg);\r\n}\r\n#menu a {\r\n	color:#FFFFFF;\r\n	text-decoration:none;\r\n	padding:10px;\r\n	padding-top:20px;\r\n	padding-bottom:20px;\r\n	\r\n}\r\n#menu a:hover {\r\n	border-bottom:1px solid #ffffff;\r\n	border-left:1px solid #728d40;\r\n	border-right:1px solid #e9ffb7;\r\n	padding-left:9px;\r\n	padding-right:9px;\r\n	color:#000000;\r\n	background-color:#FFFFFF;\r\n	background-image:url(/images/plaina_hover.jpg);\r\n}\r\n\r\n#header {\r\n	width:750px;\r\n	margin:0 auto 0 auto;\r\n	margin-bottom:40px;\r\n}\r\n#header h1 {\r\n	font-family:\"Century Gothic\", \"Trebuchet MS\", \"Arial Narrow\", Arial, sans-serif;\r\n	font-size:30px;\r\n	text-transform:uppercase;\r\n	font-weight:normal;\r\n	margin:0;\r\n	padding:0;\r\n	padding-top:5px;\r\n	color:#736451;\r\n	margin-bottom:10px;\r\n	text-align:left;\r\n}\r\n#header h2 {\r\n	font-family:\"Century Gothic\", \"Trebuchet MS\", \"Arial Narrow\", Arial, sans-serif;\r\n	font-size:15px;\r\n	text-transform:uppercase;\r\n	text-align:right;\r\n	font-weight:normal;\r\n	margin:0;\r\n	padding:0;\r\n	color:#000000;\r\n	border-bottom:1px solid #eeeeee;\r\n}\r\n\r\n#content {\r\n	width:720px;\r\n	margin:0 auto 0 auto;\r\n	text-align:left;\r\n}\r\n\r\n.introduction {\r\n	font-family:Helvetica, Arial, sans-serif;\r\n	line-height:20px;\r\n	font-size:12px;\r\n	color:#99B198;\r\n	text-indent:25px;\r\n	background-image:url(/images/plaindesc.jpg);\r\n	background-position:bottom left;\r\n	padding-left:0;\r\n	padding-right:200px;\r\n	padding-bottom:35px;\r\n	padding-top:0;\r\n	background-repeat:no-repeat;\r\n	\r\n}\r\n.logo {\r\n	float:right;\r\n	margin-right:30px;\r\n}\r\n.clearer {\r\n	clear:both;\r\n}\r\n\r\n#sidebar {\r\n	width:180px;\r\n	float:right;\r\n	font-size:95%;\r\n	color:#6C534A;\r\n	text-align:left;\r\n}\r\n#sidebar h1 {\r\n	font-family:\"Century Gothic\", \"Trebuchet MS\", \"Arial Narrow\", Arial, sans-serif;\r\n	font-size:20px;\r\n	text-transform:uppercase;\r\n	font-weight:normal;\r\n	border-bottom:1px solid #eeeeee;\r\n	color:#728c40;\r\n	text-align:right;\r\n}\r\n.submenu {\r\n}\r\n.submenu a {\r\n	display:block;\r\n	width:100%;\r\n	background-image:url(/images/plainsubmenua.gif);\r\n	background-position:right;\r\n	background-repeat:no-repeat;\r\n	padding:5px;\r\n	padding-right:0;\r\n}\r\n.submenu a:hover {\r\n	background-color:#eeeeee;\r\n	background-image:url(/images/plainsubmenua_hover.jpg);\r\n	background-repeat:repeat-y;\r\n}\r\n#mainbar {\r\n	margin-right:185px;\r\n	padding-right:35px;\r\n	background-image:url(/images/plainmainbar.jpg);\r\n	background-position:top right;\r\n	background-repeat:no-repeat;\r\n}\r\n\r\n#mainbar h1 {\r\n	font-family:\"Century Gothic\", \"Trebuchet MS\", \"Arial Narrow\", Arial, sans-serif;\r\n	font-size:25px;\r\n	text-transform:uppercase;\r\n	font-weight:normal;\r\n	border-bottom:1px solid #eeeeee;\r\n	color:#728c40;\r\n}\r\n\r\n#mainbar p {\r\n	padding:15px;\r\n	line-height:20px;\r\n}\r\n\r\n#mainbar .articleimg {\r\n	float:right;\r\n	padding:5px;\r\n	border:1px solid #eeeeee;\r\n	margin-left:20px;\r\n	margin-top:20px;\r\n	margin-bottom:20px;\r\n}\r\n\r\n#footer {\r\n	width:100%;\r\n	background-color:#b6cf84;\r\n	border-top:1px solid #d8efa6;\r\n	border-bottom:5px solid #94ae62;\r\n	padding-top:5px;\r\n	padding-bottom:5px;\r\n	color:#FFFFFF;\r\n}\r\n#footer a {\r\n	color:#006600;\r\n}\r\n#footer a:hover {\r\n	color:#009900;\r\n}\r\n','0','This is where you put comments about this content','','1','','2006-06-01 00:00:00','plain.css','admin','1010','blank','STUB Content','text/css','users');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('a {\r\n	color: #666666;\r\n	text-decoration: underline;\r\n}\r\na:hover {\r\n	color: #888888;\r\n	text-decoration: none;\r\n}\r\nacronym{\r\n	border-bottom: 1px dotted #666666;\r\n	cursor: help;\r\n}\r\nbody{\r\n	background-color: #FFFFFF;\r\n	font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;\r\n	font-size: 10px;\r\n	color: #333333;\r\n	margin: 10px;\r\n	padding: 0px;\r\n}\r\nh1{\r\n	color:#666666;\r\n	font-family: Trebuchet MS, Verdana, Arial, Helvetica, sans-serif;\r\n	font-size: 18px;	\r\n	line-height: 13px;\r\n	margin: 0px 0px 0px 10px;\r\n	padding: 0px;\r\n}\r\nh2{\r\n	color:#666666;\r\n	font-family: Trebuchet MS, Verdana, Arial, Helvetica, sans-serif;\r\n	font-size: 18px;	\r\n	line-height: 13px;\r\n	margin: 0px 10px 0px 0px;\r\n	padding: 0px;\r\n	text-align:right;\r\n}\r\nh3{\r\n	color:#666666;\r\n	font-family: Trebuchet MS, Verdana, Arial, Helvetica, sans-serif;\r\n	font-size: 14px;	\r\n	line-height: 14px;\r\n	margin: 10px 0px 3px 0px;\r\n	padding: 0px;\r\n	text-align:left;\r\n}\r\n.contentleft{\r\n	width: 77%;\r\n}\r\n.contentmain{\r\n	background-color:#F9F8F7; \r\n	padding:5px;\r\n}\r\n.contentright{\r\n	width: 23%;\r\n}\r\n.greybox{\r\n	background-color: #DEDEDE;\r\n	color:#666666;\r\n}\r\n.header{\r\n	background-color: #E6E6E6;\r\n	background: url(/images/dittoheader.jpg);\r\n	font-size: 36px;\r\n	font-family: Trebuchet MS, Verdana, Arial, Helvetica, sans-serif;\r\n	color: #903326;\r\n	line-height: 24px;\r\n	font-weight: bold;\r\n}\r\n.menubox{\r\n	background-color: #484848;\r\n	color:#F9F8F7;\r\n	font-family: Trebuchet MS, Verdana, Arial, Helvetica, sans-serif;\r\n}\r\n.menubox a{\r\n	color:#BDBDBD;\r\n	font-size: 18px;\r\n	line-height: 30px;\r\n	text-align: center;\r\n	text-decoration: none;\r\n	font-weight: bold;\r\n}\r\n.menubox a:hover{\r\n	color:#FFFFFF;\r\n	font-size: 18px;\r\n	line-height: 30px;\r\n	text-decoration: none;\r\n}\r\n.menubox a.selected{\r\n	color:#FFFFFF;\r\n}\r\n.searchbox {\r\n	font-size: 11px;\r\n	padding:1px 2px 1px 2px;\r\n	border:1px solid #999999;\r\n	color:#666666;\r\n	background:#F6F6F6;\r\n	text-align:right;\r\n}\r\n.subheader{\r\n	background-color: #903326; /*D6C43A;*/\r\n}\r\n.whitebox{\r\n	background-color: #FFFFFF;\r\n	color:#666666;\r\n}\r\n.whitebox_topleft{\r\n	background: url(/images/ditto1-topleft.gif);\r\n	width:9px;\r\n}\r\n.whitebox_bottomright{\r\n	background: url(/images/ditto1-bottomright.gif);\r\n	width:9px;\r\n}\r\n','0','This is where you put comments about this content','','1','','2006-06-01 00:00:00','ditto-screen.css','admin','1010','blank','STUB Content','text/css','users');
