insert into style (body,icomment,macros,modified,name,owner,permissions,title,usergroup) values ('<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\">\r\n\r\n<!-- Web Page Design by James Koster - http://www.jameskoster.co.uk -->\r\n\r\n<head>\r\n<meta http-equiv=\"Content-Type\" content=\"text/html; charset=iso-8859-1\" />\r\n<title><?SPINE_Title?></title>\r\n<link href=\"plain.css\" rel=\"stylesheet\" type=\"text/css\" />\r\n</head>\r\n\r\n<body>\r\n<a name=\"top\" id=\"top\"></a>\r\n<center>\r\n		<div id=\"menu\">\r\n				<?SPINE_Navbar(\"main\")?>\r\n		</div>\r\n		\r\n		<div id=\"header\">\r\n				<h1><?SPINE_Title?></h1>\r\n				<h2>Bla Bla Bla Bla (edit this in the \'plain\' style)</h2>\r\n		</div>\r\n		\r\n		<div id=\"content\">\r\n		\r\n				<img src=\"/images/plainlogo.jpg\" alt=\"Your Logo\" class=\"logo\" />\r\n				\r\n				<p class=\"introduction\">\r\nThis introduction is part of the style. Change style \'plain\' to edit this part.\r\n				</p>\r\n				\r\n				<div id=\"sidebar\">\r\n						<h1>Documentation</h1>\r\n						<div class=\"submenu\">\r\n						<?SPINE_Navbar(\"plain_docs\")?>\r\n						</div>\r\n				\r\n					<p>\r\n							You can view more of my templates on my Templates Website: <a href=\"http://xij.p0wned.org/prettyasapicture/\">Pretty as a Picture</a>					\r\n					</p>\r\n					\r\n					<h1>Log In</h1>\r\n					<?SPINE_Login?>\r\n				</div>\r\n				\r\n				<div id=\"mainbar\">\r\n						<?SPINE_Content?>\r\n		  </div>\r\n		\r\n  </div>\r\n  <div id=\"footer\">\r\n  		This is the footer. Put all your copyright info here.<br />\r\n  Page designed by <a href=\"http://www.jameskoster.co.uk\">James Koster</a>  </div>\r\n</center>\r\n</body>\r\n</html>\r\n','This design was made freely available on Open Source Web Design (http://www.oswd.org) by JayKay (http://www.oswd.org/user/profile/id/6304).\r\n','','2006-06-01 00:00:00','plain','admin','1010','','users');
