insert into style (body,icomment,macros,modified,name,owner,permissions,title,usergroup) values ('<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.1//EN\"\r\n\"http://www.w3.org/TR/xhtml11/DTD/xhtml11.dtd\">\r\n<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"en\">\r\n<head>\r\n<title><?SPINE_Title?></title>\r\n<meta http-equiv=\"content-type\" content=\"text/html; charset=iso-8859-1\" />\r\n<meta name=\"description\" content=\"Your description goes here.\" />\r\n<meta name=\"keywords\" content=\"your,keywords,goes,here\" />\r\n<meta name=\"author\" content=\"Your Name / Original design by Andreas Viklund - http://andreasviklund.com\" />\r\n<link rel=\"stylesheet\" href=\"andreas09.css\" type=\"text/css\" media=\"screen\" />\r\n</head>\r\n\r\n<body>\r\n<div id=\"container\">\r\n\r\n<div id=\"sitename\">\r\n<h1><?SPINE_Title?></h1>\r\n<h2>Being the printing and typesetting industry standard latin phrasebook for decades!</h2>\r\n</div>\r\n\r\n<div id=\"mainmenu\">\r\n<ul>\r\n<li><?SPINE_Navbar(\"andreas09_main\")?></li>\r\n</ul>\r\n</div>\r\n \r\n<div id=\"wrap\">\r\n\r\n<div id=\"leftside\">\r\n<h1>Included layouts</h1>\r\n<p>\r\n<a class=\"nav active\" href=\"index.html\">3 columns</a><span class=\"hide\"> | </span>\r\n<a class=\"nav\" href=\"2col.html\">2 columns</a><span class=\"hide\"> | </span>\r\n<a class=\"nav\" href=\"no-img.html\">No images</a><span class=\"hide\"> | </span>\r\n<a class=\"nav\" href=\"#\">Example menu</a><span class=\"hide\"> | </span>\r\n<a class=\"nav sub\" href=\"#\">Sub-page 1</a><span class=\"hide\"> | </span>\r\n<a class=\"nav sub\" href=\"#\">Active sub-page</a><span class=\"hide\"> | </span>\r\n<a class=\"nav sub\" href=\"#\">Sub-page 2</a>\r\n</p>\r\n\r\n<h1>Included colors</h1>\r\n<img src=\"/images/andreas09_colors.jpg\" height=\"104\" width=\"125\" class=\"thumbnail\" alt=\"Included colors\" />\r\n</div>\r\n\r\n<div id=\"rightside\">\r\n<h1>Search</h1>\r\n<p class=\"searchform\">\r\n<form method=\"get\" action=\"search.html\"><input type=\"text\" alt=\"Search\" class=\"searchbox\" name=\"keyword\"/>\r\n<input type=\"submit\" value=\"Go!\" class=\"searchbutton\" />\r\n</form></p>\r\n\r\n<h1>Latest news</h1>\r\n<p><strong>Dec 11: </strong>\r\nandreas09 is built with valid XHTML 1.1 and CSS2. It is simple and easy to work with, and it comes in several different flavors. This is version 1.0 (Dec 11th 2005).</p>\r\n<p><strong>Dec 10: </strong>\r\nPlace for news or important messages, maybe?</p>\r\n\r\n<h1>Links:</h1>\r\n<ul class=\"linklist\">\r\n<li><a href=\"http://andreasviklund.com\">My website</a></li>\r\n<li><a href=\"http://andreasviklund.com/templates\">Free website templates</a></li>\r\n<li><a href=\"http://openwebdesign.org\">OpenWebDesign.org</a></li>\r\n<li><a href=\"http://oswd.org\">OSWD.org</a></li>\r\n</ul>\r\n</div>\r\n\r\n<div id=\"content\">\r\n<?SPINE_Content?>\r\n</div>\r\n<div class=\"clearingdiv\">&nbsp;</div>\r\n</div>\r\n</div>\r\n<div id=\"footer\">&copy; 2005 Your Name | Design by <a href=\"http://andreasviklund.com\">Andreas Viklund</a></div>\r\n</body>\r\n</html>','This is where you put comments about this style','','2006-06-13 00:38:37','andreas09','admin','1010','','users');
