insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('body, td, div, font {\r\n	font-family: Verdana, Arial, Helvetica, sans-serif;\r\n	font-size: 9pt;\r\n	color: #224466;\r\n}\r\n\r\nbody {\r\n	background: #dddddd;\r\n        background-image: url(<?SPINE_Images?>spine4.jpg);\r\n        background-repeat: repeat-x;\r\n	margin: 0;\r\n	padding: 0;\r\n}\r\n\r\na {\r\n	color: #000000;\r\n	text-decoration: none;\r\n	border-bottom: 1px solid #000000;\r\n	position: relative;\r\n}\r\n\r\na:hover {\r\n	background: #f3f3ff;\r\n	font-weight: bold;\r\n	color: #000000;\r\n	border: 2px ridge #446688;\r\n	margin: -4px;\r\n	padding: 2px;\r\n	z-index: 10;\r\n}\r\n\r\n#menu {\r\n	background: #dddddd url(<?SPINE_Images?>spine4.jpg) repeat-x;\r\n	border-bottom: 1px solid #000000;\r\n	padding-left: 1em;\r\n}\r\n\r\n#menu a {\r\n	font-weight: 500;\r\n	font-size: 8pt;\r\n	padding: 10px 5px;\r\n	color: #223355;\r\n	border: 0;\r\n	margin: 0;\r\n	position: relative;\r\n}\r\n\r\n#menu a:hover {\r\n}\r\n\r\n#footer {\r\n	padding: 0.5em;\r\n	padding-left: 2em;\r\n}\r\n\r\nh2 {\r\n	margin: 0;\r\n	padding: 1em;\r\n        padding-bottom: 0.6em;\r\n	font-size: 10pt;\r\n}\r\n\r\nh3 {\r\n	margin: 0;\r\n	padding: 1em;\r\n        padding-bottom: 0.6em;\r\n	font-size: 9pt;\r\n}\r\n\r\n#container {\r\n	margin-left: 1px;\r\n	padding: 1em;\r\n	padding-right: 3em;\r\n	background: #fff;\r\n	font-size: 9pt;\r\n	border-bottom: 1px solid #000;\r\n}\r\n\r\n#version {\r\n	font-size: 17pt;\r\n	color: #446677;\r\n	position: absolute;\r\n	top: 1em;\r\n	left: 100%;\r\n	margin-left: -150pt;\r\n}\r\n\r\ndiv.section-body {\r\n	margin: 1em 2em;\r\n}\r\n\r\n#content { line-height: 140%; }\r\n\r\n/* Front Page News */\r\n\r\nh2.news {\r\n	border-bottom: 1px solid #CCC;\r\n	padding-top: 1em;\r\n	padding-bottom: 0.4em;\r\n	font-size: 1.2em;\r\n	border-bottom: 1px solid #000;\r\n}\r\n\r\ndiv.newsdate {\r\n	text-align: right;\r\n	color: #444;\r\n	font-weight: bold;\r\n	font-size: 0.95em;\r\n}\r\n\r\ndiv.newsdate a:link	{ color: #666; text-decoration: none; }\r\ndiv.newsdate a:visited	{ color: #4462b7; text-decoration: none; }\r\ndiv.newsdate a:hover	{ color: #00C; text-decoration: underline; }\r\ndiv.newsdate a:active	{ color: #000; text-decoration: underline; }\r\n\r\n.button { \r\n     font-family: Verdana, Arial, Helvetica, sans-serif; \r\n     font-size: 8pt; \r\n     color: #ffffff; \r\n     background-color: #6590fd; \r\n     border: 1px solid #000000; \r\n     width: 70pt \r\n}\r\n \r\n.input {\r\n	font-family: Verdana, Arial, Helvetica, sans-serif;\r\n        font-size: 8pt; \r\n	color: #000000;\r\n	border: 1px solid #000000;\r\n	width: auto;\r\n	font-variant: normal;\r\n}\r\n\r\npre.block {\r\n background:  #DCFAEA;\r\n border: 1px solid #777777;\r\n margin: 1em 1.75em;\r\n padding: .25em;\r\n overflow: auto;\r\n}\r\n\r\n.panelcel\r\n{ border: 0px solid; \r\n  float: left; \r\n  margin: 0; \r\n  padding: 0px;\r\n  height: auto;\r\n  font-family: Verdana, Arial, Helvetica, sans-serif;\r\n  font-size: 12px;\r\n  color: #000000;\r\n}\r\n\r\n.spacercel\r\n{ clear: both; \r\n  margin: 0; \r\n  padding: 1px; \r\n  width: auto;\r\n}\r\n\r\nform\r\n{ padding: 0; \r\n  margin: 0; \r\n  display: inline;\r\n}\r\n','0','This style is part of the default SPINE Installation.\r\nCreated by Hendrik Van Belleghem ; Major Updates by eidolon','','1','main','2008-07-08 19:56:54','spine.css','admin','1010','blank','','text/css','users');
