insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('body\r\n{\r\n	background-color: #665;	/* border/edge of the layout color */\r\n}\r\n#pageFrame, #masthead, #footer\r\n{	\r\n	background-color: #fff;	/* major background color */\r\n}\r\n#pageFrame\r\n{\r\n	/* image used as background color for left column ( #E9E8CA ) */\r\n	background: #e9e8cA;\r\n}\r\n#masthead, #footer\r\n{\r\n	background-color: #885;\r\n	color: #fff;\r\n}\r\n#footer\r\n{\r\n	border-bottom: solid 1px #997; /* IE 6 has the footer flow over the pageFrame\'s \r\n									  bottom border. if this border is added, IE draws\r\n									  the pageFrame border as exptected */\r\n}\r\n.hnav, .hnav ul li a\r\n{\r\n	background-color: #cc9;\r\n	color: #336;\r\n}\r\n.hnav ul li a:hover\r\n{\r\n	background-color: #336;\r\n	color: #fff;\r\n}\r\n.vnav ul li a\r\n{\r\n	color: #336;\r\n	background-color: #cc9;\r\n}\r\n.vnav ul li a:hover\r\n{\r\n	background-color: #336;\r\n	color: #fff;\r\n}\r\n','0','This style is part of the design released into public domain:\r\nhttp://webhost.bridgew.edu/etribou/layouts/2col_footer/index.html','','1','main','2006-12-31 23:00:00','.ruthcolors.css','admin','1010','blank','','text/css','users');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('/*---- Layout ----*/\r\ndiv\r\n{\r\n	/* this is for netscape 4\'s sake */\r\n	padding: 0;\r\n	margin: 0;\r\n}\r\nbody\r\n{\r\n	padding: 20px;\r\n	margin: 0;\r\n}\r\n#oldbrowser\r\n{\r\n	text-align: center;\r\n}\r\n#masthead h1\r\n{\r\n	display: inline;\r\n}\r\n#leftcolumn\r\n{\r\n	margin-right: -200px;	/* IE 4 fix */\r\n}\r\n#leftColumn\r\n{\r\n	float: left;\r\n	width: 200px;\r\n	/*/*/ /*/margin: 0 0 0 2px; /* Silly Netscape hack to get the borders to line up */\r\n}\r\n#contentColumn\r\n{\r\n	width: auto;\r\n	margin-left: 199px;\r\n}\r\n#masthead, #innerLeftColumn, #innerContentColumn, #innerFooter\r\n{\r\n	padding: 10px;\r\n}\r\n#footer\r\n{\r\n	clear: left;\r\n}\r\n#innerFooter\r\n{\r\n	text-align: center;\r\n}\r\n#innerContentColumn\r\n{\r\n	overflow: visible;\r\n	height: 100%;	/* fix the Win32 IE float bug */\r\n}\r\n#contentColumn>#innerContentColumn\r\n{\r\n	height: auto;	/* fix Opera 5 which breaks with the above IE fix */\r\n}\r\n#innercontentcolumn\r\n{\r\n	height: 100%;	/* fix IE 5.0 which parse the Opera fix, note the selector is all lower case */\r\n}\r\n#masthead, #footer\r\n{\r\n	z-index: 10;\r\n}\r\n\r\n/*---- Borders ----*/\r\n#pageFrame\r\n{\r\n	border: solid 1px #000;\r\n}\r\n#footer, #masthead, #innerLeftColumn, #contentColumn, .hnav\r\n{\r\n	border: solid 0px #000;\r\n}\r\n#footer\r\n{\r\n	border-top-width: 1px;\r\n}\r\n#masthead, .hnav\r\n{\r\n	border-bottom-width: 1px;\r\n}\r\n#innerLeftColumn\r\n{\r\n	border-right-width: 1px;\r\n}\r\n#contentColumn\r\n{\r\n	border-left-width: 1px;\r\n}\r\n\r\n/*---- Visual Elements ----*/\r\nbody\r\n{\r\n	background-color: #ddd;\r\n	color: #000;\r\n	padding: 20px;\r\n	margin: 0;\r\n}\r\n#pageFrame\r\n{\r\n	background-color: #fff;\r\n	color: #000;\r\n	min-width: 500px;\r\n}\r\n.hide\r\n{\r\n	display: none;\r\n}\r\n\r\n/*---- Simple Horizontal Navigation for IE 4/5.0/NS4 ----*/\r\n.hnav\r\n{\r\n	white-space: nowrap;\r\n	margin: 0;\r\n	color: #000;\r\n}\r\n.hnav\r\n{\r\n	padding: 3px 0 4px 0;\r\n}\r\n.hnav ul\r\n{\r\n	text-align: center;\r\n	list-style-type: none;\r\n	line-height: normal;\r\n	margin: 0;\r\n	padding: 0;\r\n}\r\n.hnav ul li\r\n{\r\n	display: inline;\r\n	white-space: nowrap;\r\n	/*/*/ /*/display: none;/*  */\r\n}\r\n','0','This style is part of the design released into public domain:\r\nhttp://webhost.bridgew.edu/etribou/layouts/2col_footer/index.html','','1','main','2006-12-31 23:00:00','.ruthsimple.css','admin','1010','blank','','text/css','users');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('div#oldbrowser\r\n{\r\n	display: none;	/* hide the standards compliant warning - this isn\'t a catch all, but works for old browsers */\r\n}\r\na img\r\n{\r\n	border-width: 0;	/* remove the border around linked images. <img border=\"0\" ..../> is invalid in XHTML 1.1 */\r\n}\r\n#innerLeftColumn img\r\n{\r\n	display: block;\r\n	margin: 0 auto;\r\n	text-align: center;	/* IE 5 centering hack */\r\n}\r\n#innerLeftColumn, #innerContentColumn\r\n{\r\n	overflow: visible;	/* fix the Win32 IE italics bug */\r\n	height: 100%;		/* ditto */\r\n}\r\n#innerleftcolumn, #innercontentcolumn\r\n{\r\n	overflow: hidden;	/* IE5.0 fix the Win32 IE italics bug */\r\n	width: 100%;\r\n	height: auto;\r\n}\r\n#innerleftcolumn/* hide from IE5.0 */, #innercontentcolumn/* hide from IE5.0 */\r\n{\r\n	overflow: visible;	/* reset IE5.0 fix for 5.5 */\r\n	height: 100%;		/* ditto */\r\n	width: auto;		/* reset Safair after picking up the above IE5.0 fix : it\'s case insensitive to CSS selectors */\r\n}\r\n* html #masthead\r\n{\r\n	position: relative;\r\n}','0','This style is part of the design released into public domain:\r\nhttp://webhost.bridgew.edu/etribou/layouts/2col_footer/index.html','','1','main','2006-12-31 23:00:00','.ruthcomplex.css','admin','1010','blank','','text/css','users');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('.vnav ul, .vnav ul li\r\n{\r\n	margin: 0;\r\n	padding: 0;\r\n	list-style-type: none;\r\n	display: block;\r\n}\r\n.vnav ul\r\n{\r\n	border: solid 1px #000;\r\n	border-bottom-width: 0;\r\n}\r\n.vnav ul li\r\n{\r\n	border-bottom: solid 1px #000;\r\n}\r\n.vnav ul li a\r\n{\r\n	display: block;\r\n	text-decoration: none;\r\n	padding: 2px 10px;\r\n}\r\n/* Hide from IE-mac */\r\n* html .vnav ul li a/* hide from IE 5.0 */ {height: 1%;}\r\n/* End hide */\r\n','0','This style is part of the design released into public domain:\r\nhttp://webhost.bridgew.edu/etribou/layouts/2col_footer/index.html','','1','main','2006-12-31 23:00:00','.ruthcomplex_vnav.css','admin','1010','blank','','text/css','users');
insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('/*** Complex Horizontal Navigation ***/\r\n.hnav ul li\r\n{\r\n	margin: 0;\r\n}\r\n.hnav ul li a\r\n{\r\n	text-decoration: none;\r\n	color: #000;\r\n	background-color: #eee;\r\n	margin: 0 -1px 0 0;\r\n	padding: 3px 10px 4px 10px;\r\n	border-left: solid 1px #000;\r\n	border-right: solid 1px #000;\r\n}\r\n.hNAV\r\n{\r\n	padding: 0;		/*	IE5/Win will resize hnav to fit the heights of its\r\n						inline children that have vertical padding. So this\r\n						incorrect case selector will remove that padding */\r\n}\r\n.HNAV ul li a\r\n{\r\n	height: 1%;		/*	holly hack for IE5/Win because 5.0 doesn\'t recognize \r\n						inline padding by default */\r\n}\r\nhtml>body .HNAV\r\n{\r\n	padding: 3px 0 4px 0;	/* Fix for Safari which is case-insensitive */\r\n	height: auto;\r\n}\r\n.hnav ul li a:hover\r\n{\r\n	text-decoration: underline;\r\n}\r\n.hnav ul li a:hover\r\n{\r\n	text-decoration: none;\r\n}\r\n.hnav ul li span\r\n{\r\n	display: none;\r\n}\r\n\r\n\r\n','0','This style is part of the design released into public domain:\r\nhttp://webhost.bridgew.edu/etribou/layouts/2col_footer/index.html','','1','main','2006-12-31 23:00:00','.ruthcomplex_hnav.css','admin','1010','blank','','text/css','users');
