insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('/*\r\n	NS4 doesn\'t do well with added colors because of the extra padding added inside each\r\n	block element. So colors are imported. This theme file will, thus, contain mostly\r\n	font stuff.\r\n */\r\n@import \".ruthcolors.css\";\r\n\r\n/*** Left Column ***/\r\n#innerLeftColumn p\r\n{\r\n	font-family: Georgia, \"Times New Roman\", serif;\r\n	font-size: 70%;\r\n	line-height: 140%;\r\n	/*/*/ /*/line-height: normal;/* NS4 Adds too much space, so reset the line-height */\r\n}\r\n#innerLeftColumn h3\r\n{\r\n	color: #006;\r\n	margin: 1em 0 0 0;\r\n}\r\n\r\n/*** Content ***/\r\n#innerContentColumn\r\n{\r\n	font-family: Georgia, \"Times New Roman\", serif;\r\n}\r\n#innerContentColumn h3\r\n{\r\n	color: #009;\r\n}\r\n\r\n/*** Masthead ***/\r\n#masthead h1\r\n{\r\n	font-family: Georgia, \"Times New Roman\", serif;\r\n}\r\n\r\n/*** Footer ***/\r\n#innerFooter\r\n{\r\n	font-family: Georgia, \"Times New Roman\", serif;\r\n}\r\n\r\n/*** Horizontal Navigation Area ***/\r\n.hnav\r\n{\r\n	font-family: verdana, helvetica, sans-serif;\r\n	font-weight: bold;\r\n	font-size: 70%;\r\n}\r\n\r\n/*** Vertical Navigation Area ***/\r\n.vnav\r\n{\r\n	font-family: verdana, helvetica, sans-serif;\r\n	font-weight: bold;\r\n	font-size: 70%;\r\n	line-height: 180%; /* Mozilla suffers from a rounding problem, \r\n						  this value may have to be tweaked if you \r\n						  change the font family or size \r\n						*/\r\n	/*/*/ /*/line-height: 120%;/* NS4 Adds too much space, so lower it */\r\n}\r\n','0','This CSS is part of the design released into public domain:\r\nhttp://webhost.bridgew.edu/etribou/layouts/2col_footer/index.html','','1','main','2006-12-31 23:00:00','.ruththeme.css','admin','1010','blank','','text/css','users');
