insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('/* CSS Document */\r\n\r\nbody {\r\n	margin:0;\r\n	font-family:\"Trebuchet MS\", Arial, Verdana;\r\n	font-size:0.74em;\r\n	color:#555555;\r\n	background-color:#FFFFFF;\r\n	text-align:left;\r\n}\r\n\r\ninput {\r\n	background-color:#FFFFFF;\r\n	padding:3px;\r\n	font-family:Verdana, Arial, Helvetica, sans-serif;\r\n	font-size:10px;\r\n	color:#ffffff;\r\n	border:1px solid #728c40;\r\n	background-color:#94ae62;\r\n	margin-bottom:2px;\r\n}\r\ninput[type=submit] {\r\n	background-color:#FFFFFF;\r\n	padding:3px;\r\n	font-family:Verdana, Arial, Helvetica, sans-serif;\r\n	font-size:10px;\r\n	color:#666666;\r\n	border:1px solid #728c40;\r\n	margin-bottom:2px;\r\n}\r\n\r\na {\r\n	color:#4D87CE;\r\n	font-weight:bold;\r\n	text-decoration:none;\r\n}\r\na:hover {\r\n	color:#2a65ad;\r\n}\r\n\r\nol,ul {\r\n\r\n}\r\n\r\nli {\r\n	list-style-image:url(<?SPINE_Images?>plainli.gif);\r\n	line-height:20px;\r\n	text-align:left;\r\n}\r\n\r\n#menu {\r\n	width:100%;\r\n	padding-top:20px;\r\n	padding-bottom:20px;\r\n	background-color:#94AE62;\r\n	margin-bottom:50px;\r\n	color:#FFFFFF;\r\n	text-align:center;\r\n	font-family:Verdana, Arial, Helvetica, sans-serif;\r\n	font-size:11px;\r\n	font-variant:small-caps;\r\n	background-image:url(<?SPINE_Images?>plainmenu.jpg);\r\n}\r\n#menu a {\r\n	color:#FFFFFF;\r\n	text-decoration:none;\r\n	padding:10px;\r\n	padding-top:20px;\r\n	padding-bottom:20px;\r\n	\r\n}\r\n#menu a:hover {\r\n	border-bottom:1px solid #ffffff;\r\n	border-left:1px solid #728d40;\r\n	border-right:1px solid #e9ffb7;\r\n	padding-left:9px;\r\n	padding-right:9px;\r\n	color:#000000;\r\n	background-color:#FFFFFF;\r\n	background-image:url(<?SPINE_Images?>plaina_hover.jpg);\r\n}\r\n\r\n#header {\r\n	width:750px;\r\n	margin:0 auto 0 auto;\r\n	margin-bottom:40px;\r\n}\r\n#header h1 {\r\n	font-family:\"Century Gothic\", \"Trebuchet MS\", \"Arial Narrow\", Arial, sans-serif;\r\n	font-size:30px;\r\n	text-transform:uppercase;\r\n	font-weight:normal;\r\n	margin:0;\r\n	padding:0;\r\n	padding-top:5px;\r\n	color:#736451;\r\n	margin-bottom:10px;\r\n	text-align:left;\r\n}\r\n#header h2 {\r\n	font-family:\"Century Gothic\", \"Trebuchet MS\", \"Arial Narrow\", Arial, sans-serif;\r\n	font-size:15px;\r\n	text-transform:uppercase;\r\n	text-align:right;\r\n	font-weight:normal;\r\n	margin:0;\r\n	padding:0;\r\n	color:#000000;\r\n	border-bottom:1px solid #eeeeee;\r\n}\r\n\r\n#content {\r\n	width:720px;\r\n	margin:0 auto 0 auto;\r\n	text-align:left;\r\n}\r\n\r\n.introduction {\r\n	font-family:Helvetica, Arial, sans-serif;\r\n	line-height:20px;\r\n	font-size:12px;\r\n	color:#99B198;\r\n	text-indent:25px;\r\n	background-image:url(<?SPINE_Images?>plaindesc.jpg);\r\n	background-position:bottom left;\r\n	padding-left:0;\r\n	padding-right:200px;\r\n	padding-bottom:35px;\r\n	padding-top:0;\r\n	background-repeat:no-repeat;\r\n	\r\n}\r\n.logo {\r\n	float:right;\r\n	margin-right:30px;\r\n}\r\n.clearer {\r\n	clear:both;\r\n}\r\n\r\n#sidebar {\r\n	width:180px;\r\n	float:right;\r\n	font-size:95%;\r\n	color:#6C534A;\r\n	text-align:left;\r\n}\r\n#sidebar h1 {\r\n	font-family:\"Century Gothic\", \"Trebuchet MS\", \"Arial Narrow\", Arial, sans-serif;\r\n	font-size:20px;\r\n	text-transform:uppercase;\r\n	font-weight:normal;\r\n	border-bottom:1px solid #eeeeee;\r\n	color:#728c40;\r\n	text-align:right;\r\n}\r\n.submenu {\r\n}\r\n.submenu a {\r\n	display:block;\r\n	width:100%;\r\n	background-image:url(<?SPINE_Images?>plainsubmenua.gif);\r\n	background-position:right;\r\n	background-repeat:no-repeat;\r\n	padding:5px;\r\n	padding-right:0;\r\n}\r\n.submenu a:hover {\r\n	background-color:#eeeeee;\r\n	background-image:url(<?SPINE_Images?>plainsubmenua_hover.jpg);\r\n	background-repeat:repeat-y;\r\n}\r\n#mainbar {\r\n	margin-right:185px;\r\n	padding-right:35px;\r\n	background-image:url(<?SPINE_Images?>plainmainbar.jpg);\r\n	background-position:top right;\r\n	background-r