orner images. */\r\n.TopRight\r\n{\r\n	float:right;\r\n	height:11px;\r\n	width:11px;\r\n	background-repeat:no-repeat;\r\n	background-image:url(<?SPINE_Images?>gscape_CrTopRight.gif);\r\n}\r\n.TopLeft\r\n{\r\n	float:left;\r\n	height:11px;\r\n	width:11px;\r\n	background-repeat:no-repeat;\r\n	background-image:url(<?SPINE_Images?>gscape_CrTopLeft.gif);\r\n}\r\n.BottomRight\r\n{\r\n	margin:-11px 0 0 0;\r\n	float:right;\r\n	height:11px;\r\n	width:11px;\r\n	background-repeat:no-repeat;\r\n	background-image:url(<?SPINE_Images?>gscape_CrBottomRight.gif);\r\n}\r\n.BottomLeft\r\n{\r\n	margin:-11px 0 0 0;\r\n	float:left;\r\n	height:11px;\r\n	width:11px;\r\n	background-repeat:no-repeat;\r\n	background-image:url(<?SPINE_Images?>gscape_CrBottomLeft.gif);\r\n}\r\n\r\n/*The general class for the contents in the right column. */\r\n.RightContents\r\n{\r\n	margin:0 auto;\r\n	padding:8px 7px 8px 7px;\r\n	background-color:#F2F3F7;\r\n	border:1px solid #899aba;\r\n	line-height:1.4em;\r\n}\r\n.RightContents h3\r\n{\r\n	margin: 5px 0 0 0;\r\n	padding: 0 0 8px 0;\r\n	background-color:inherit;\r\n	color:#575F9F;\r\n	text-align:left;\r\n	font-size: 1.3em;\r\n}\r\n.RightContents h4\r\n{\r\n	font-size: 1em;\r\n	margin: 5px 0 0 0;\r\n	background-color:inherit;\r\n}\r\n.RightContents p\r\n{\r\n	margin: 0 0 14px 0;\r\n	text-align: justify;\r\n	background-color:inherit;\r\n	color: #000000;\r\n	font-size: 90%;\r\n}\r\n.RightContents a\r\n{\r\n	background-color:inherit;\r\n	text-decoration: none;\r\n	color: #000066;\r\n}\r\n\r\n/*The class for comments in the main contents. */\r\n.Comments\r\n{\r\n	background:inherit;\r\n	border-top:solid 1px #dcdcdc;\r\n	border-bottom:solid 1px #dcdcdc;\r\n	text-align:right;\r\n	color: #000000;\r\n	font-size:1em;\r\n}\r\n\r\n/*Classes for aligning the images in the main conetes. */\r\n.AlignLeft\r\n{\r\n	margin: 10px 10px 5px 0;\r\n	float: left;\r\n}\r\n.AlignRight\r\n{\r\n	margin: 10px 0 0 10px;\r\n	float: right;\r\n}\r\n\r\n/*Class filling the left column. */\r\n.