insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('<?xml version=\"1.0\"?>\r\n<rss version=\"2.0\">\r\n  <channel>\r\n    <title>Lift Off News</title>\r\n    <link>http://liftoff.msfc.nasa.gov/</link>\r\n    <description>Liftoff to Space Exploration.</description>\r\n    <language>en-us</language>\r\n    <pubDate>Tue, 10 Jun 2003 04:00:00 GMT</pubDate>\r\n    <lastBuildDate>Tue, 10 Jun 2003 09:41:01 GMT</lastBuildDate>\r\n    <docs>http://blogs.law.harvard.edu/tech/rss</docs>\r\n    <generator>Weblog Editor 2.0</generator>\r\n    <managingEditor>editor@example.com</managingEditor>\r\n    <webMaster>webmaster@example.com</webMaster>\r\n    <ttl>5</ttl>\r\n \r\n    <item>\r\n      <title>Star City</title>\r\n      <link>http://liftoff.msfc.nasa.gov/news/2003/news-starcity.asp</link>\r\n      <description>How do Americans get ready to work with Russians aboard the\r\n        International Space Station? They take a crash course in culture, language\r\n        and protocol at Russia\'s Star City.</description>\r\n      <pubDate>Tue, 03 Jun 2003 09:39:21 GMT</pubDate>\r\n      <guid>http://liftoff.msfc.nasa.gov/2003/06/03.html#item573</guid>\r\n    </item>\r\n \r\n    <item>\r\n      <title>Space Exploration</title>\r\n      <link>http://liftoff.msfc.nasa.gov/</link>\r\n      <description>Sky watchers in Europe, Asia, and parts of Alaska and Canada\r\n        will experience a partial eclipse of the Sun on Saturday, May 31st.</description>\r\n      <pubDate>Fri, 30 May 2003 11:06:42 GMT</pubDate>\r\n      <guid>http://liftoff.msfc.nasa.gov/2003/05/30.html#item572</guid>\r\n    </item>\r\n \r\n    <item>\r\n      <title>The Engine That Does More</title>\r\n      <link>http://liftoff.msfc.nasa.gov/news/2003/news-VASIMR.asp</link>\r\n      <description>Before man travels to Mars, NASA hopes to design new engines\r\n        that will let us fly through the Solar System more quickly.  The proposed\r\n        VASIMR engine would do that.</description>\r\n      <pubDate>Tue, 27 May 2003 08:37:32 GMT</pubDate>\r\n      <guid>http://liftoff.msfc.nasa.gov/2003/05/27.html#item571</guid>\r\n    </item>\r\n \r\n    <item>\r\n      <title>Astronauts\' Dirty Laundry</title>\r\n      <link>http://liftoff.msfc.nasa.gov/news/2003/news-laundry.asp</link>\r\n      <description>Compared to earlier spacecraft, the International Space\r\n        Station has many luxuries, but laundry facilities are not one of them.\r\n        Instead, astronauts have other options.</description>\r\n      <pubDate>Tue, 20 May 2003 08:56:02 GMT</pubDate>\r\n      <guid>http://liftoff.msfc.nasa.gov/2003/05/20.html#item570</guid>\r\n    </item>\r\n  </channel>\r\n</rss>\r\n','0','This is where you put comments about this content','','1','main','2008-05-07 00:29:04','feed.rss2','admin','1010','blank','','application/rss-xml','users');
