insert into content (body,breaks,icomment,keywords,logging,macros,modified,name,owner,permissions,style,title,type,usergroup) values ('body\r\n{\r\n  background: #e4e4e4 url(../images/antique_modern_background.gif) repeat-x top left;\r\n	margin: 0px; \r\n	padding: 0px;\r\n	font-family: \"Trebuchet MS\", Verdana, sans-serif;\r\n	font-size: 12px;\r\n}\r\n\r\n#head\r\n{\r\n  background: #e4e4e4 url(../images/antique_modern_head_empty.gif) no-repeat top left;\r\n	width: 747px;\r\n	height: 117px;\r\n	padding: 0px;\r\n	margin: 0 auto;\r\n}\r\n\r\n#head #title\r\n{\r\n	float: left;\r\n 	font-size: 30px;\r\n	color: #F0EBE7;\r\n	padding: 35px 0px 0px 30px;\r\n\r\n}\r\n\r\n#body_wrapper\r\n{\r\n  background: url(../images/antique_modern_body_cont.jpg) repeat-y top left;\r\n	margin: 0px auto;\r\n	width: 747px;\r\n	display: block;\r\n}\r\n\r\n\r\n#body\r\n{\r\n  background: url(../images/antique_modern_body_top.jpg) no-repeat top left;\r\n	float: left;\r\n	padding: 10px 20px 0px 20px;\r\n	min-height: 343px;\r\n	display: block;\r\n}\r\n\r\n#end_body\r\n{\r\n  background: url(../images/antique_modern_footer.jpg) no-repeat top left;\r\n	margin: 0px auto;\r\n	width: 747px;\r\n	height: 37px;\r\n	display: block;\r\n}\r\n\r\n.clearer\r\n{\r\n  clear: both;\r\n	height: 1px;\r\n}\r\n\r\n#all\r\n{\r\n  width: 707px;\r\n	float: left;\r\n	padding: 0px;\r\n	background: url(../images/antique_modern_all_middle.gif) repeat-y top left;\r\n}\r\n\r\n#all .top\r\n{\r\n  background: url(../images/antique_modern_all_top.gif) no-repeat top left;\r\n	width: 707px;\r\n	height: 18px;\r\n	padding: 0px;\r\n	margin: 0px;\r\n}\r\n\r\n#all .bottom\r\n{\r\n  background: url(../images/antique_modern_all_bottom.gif) no-repeat bottom left;\r\n	width: 707px;\r\n	height: 21px;\r\n	padding: 0px;\r\n	margin: 0px;\r\n}\r\n\r\n#left\r\n{\r\n  width: 538px;\r\n	float: left;\r\n	padding: 0px;\r\n	margin: 0px;\r\n	background: url(../images/antique_modern_left_middle.gif) repeat-y top left;\r\n}\r\n\r\n#left .top\r\n{\r\n