ecoration : none;\r\n	font-weight: bold;\r\n	display: block;\r\n	border-style:none;\r\n}\r\n\r\n#menu ul li a:link, #menu ul li a:visited \r\n{\r\n	background-image: url(../images/antique_modern_menu_item2.gif);\r\n	background-repeat: no-repeat;\r\n	height: 26px;\r\n	width: 94px;\r\n	line-height: 26px;\r\n	padding-top: 7px;\r\n}\r\n\r\n#menu ul li a:hover, #menu ul li a:active \r\n{\r\n	background-image: url(../images/antique_modern_menu_item2.gif);\r\n	background-repeat: no-repeat;\r\n	background-position: 0px -33px;\r\n	height: 29px;\r\n	width: 94px;\r\n	line-height: 29px;\r\n	padding-top: 4px;\r\n}\r\n\r\n#menu ul li.active a:hover, #menu ul li.active a:active, #menu ul li.active a:link, #menu ul li.active a:visited \r\n{\r\n	background-image: url(../images/antique_modern_menu_item2.gif);\r\n	background-repeat: no-repeat;\r\n	background-position: 0px -66px;\r\n	height: 29px;\r\n	width: 94px;\r\n	line-height: 29px;\r\n	padding-top: 4px;\r\n	color: #B05128;\r\n}\r\n\r\n\r\n/*  Headers  */\r\nh1 \r\n{\r\n	padding: 0px;\r\n	margin: 0px;\r\n	margin-bottom: 5px;\r\n	color: #D96B11;\r\n	background-color: transparent;\r\n	font-size: 1.6em;\r\n	font-weight: bold;\r\n}\r\n\r\nh2 \r\n{\r\n	padding: 0px;\r\n	margin: 0px;\r\n	margin-top: 10px;\r\n	margin-bottom: 5px;\r\n	color: #333;\r\n	background-color: transparent;\r\n	font-size: 1.5em;\r\n	font-weight: bold;\r\n}\r\n\r\nh3 \r\n{\r\n	padding: 0px;\r\n	margin: 0px;\r\n	margin-bottom: 5px;\r\n	color: #76b7d7;\r\n	background-color: transparent;\r\n	font-size: 1.4em;\r\n	font-weight: bold;\r\n}\r\n\r\nh4 \r\n{\r\n	padding: 0px;\r\n	margin: 0px;\r\n	margin-bottom: 5px;\r\n	color: #333;\r\n	background-color: transparent;\r\n	font-size: 1.3em;\r\n	font-weight: bold;\r\n}\r\n\r\nh5 \r\n{\r\n	color: #9ad5f3;\r\n	background-color: transparent;\r\n	padding: 0px;\r\n	font-size: 1.2em;\r\n	font-weight: bold;\r\n	margin: 0px;\r\n	padding: 0px;\r\n	margin-bottom: 5px;\r\n}\r\n\r\nh6 \r\n{\r\n	padding: 0px;\r\n	margin-top: 0px;\r\n	margin-right: 0px;\r\n	margin-bottom: 5px;\r\n	margin-left: 0px;\r\n	color: #D96B11;\r\n	font-size: 1.1em;\r\n	font-weight: normal;\r\n	line-height: 150%;\r\n}\r\n\r\n/* HTML Tags */\r\n\r\ncode\r\n{\r\n  display: block;\r\n	margin: 5px;\r\n	padding: 5px;\r\n	color: #ad4c21;\r\n	border: 1px dashed;\r\n	background: #f0ebe7;\r\n	font-family: Courier, \'Courier New\', \'Andale Mono\', Monaco, monospace;\r\n\r\n}\r\n\r\na\r\n{\r\n  color: #AD4C21;\r\n	text-decoration: none;\r\n	border-bottom: 1px dashed;\r\n	padding-bottom: 0px;\r\n}\r\n\r\na:hover\r\n{\r\n	color: #000;\r\n}\r\n\r\nhr\r\n{\r\n  border: 0px;\r\n  border-bottom: 1px #F0EBE7 dashed;\r\n	height: 1px;\r\n}\r\n\r\n#left hr, #all hr\r\n{\r\n  margin: 10px 0px;\r\n}\r\n\r\nform\r\n{\r\n  margin: 0px;\r\n	padding: 0px;\r\n}\r\n\r\nform .item\r\n{\r\n  clear: left;\r\n	border-top: 1px solid #EEE;\r\n	margin: 3px;\r\n}\r\n\r\nform .label\r\n{\r\n  float: left;\r\n	clear: left;\r\n	width: 150px;\r\n}\r\n\r\ninput, textarea, select\r\n{\r\n  color: #AD4C21;\r\n	border: 1px solid;\r\n	background: #F0EBE7;\r\n	margin: 3px;\r\n	padding: 2px;\r\n}\r\n\r\nblockquote {\r\n	margin: 5px;\r\n  background: transparent url(../images/antique_modern_quotes.gif) left top no-repeat;\r\n	padding-left: 37px;\r\n}\r\n\r\nblockquote[cite]:after {\r\n	content: \"Quote from: \" attr(cite);\r\n	display: block;\r\n	border-top: 1px solid #999;\r\n	color: #999;\r\n	margin: 1em 0 0;\r\n	padding: .5em 0 0;\r\n	font-size: .8em;\r\n	font-weight: bold;\r\n}\r\n\r\ndel \r\n{\r\n  color: #800;\r\n	text-decoration: line-through;\r\n}\r\n\r\ndt\r\n{\r\n  font-weight: bold;\r\n	font-size: 1.05em;\r\n	color: #ad4c21;\r\n}\r\n\r\ndd \r\n{\r\n  margin-left: 15px;\r\n}\r\n\r\n/* adds a space and then a graphic after any external (i.e. off-site) links */\r\na[target]:after{\r\n	content: \" \" url(../images/antique_modern_externalpage.gif);\r\n}','0','This is where you put comments about this content','','1','main','2008-04-22 21:59:25','antique_modern_style.css